{% if blog.metafields.custom.desktop_banner or blog.metafields.custom.mobile_banner %}
  <style>
    .blog-banner {
      width: 100%;
      overflow: hidden;
    }

    .blog-banner img {
      width: 100%;
      height: auto;
      display: block;
    }

    @media screen and (min-width: 1024px) {
      .blog-banner.desktop-banner {
        display: block;
        height: auto;
      }

      .blog-banner.mobile-banner {
        display: none;
      }

      .blog-banner.desktop-banner img {
        height: 100%;
        object-fit: cover;
      }
    }

    @media screen and (max-width: 1023px) {
      .blog-banner.desktop-banner {
        display: none;
      }

      .blog-banner.mobile-banner {
        display: block;
width:100%;
        height: auto;
      }

      .blog-banner.mobile-banner img {
width:100%;
        height: 100%;
        object-fit: cover;
      }
    }
  </style>

  {% if blog.metafields.custom.desktop_banner %}
    <div class="blog-banner desktop-banner">
      <img
        src="{{ blog.metafields.custom.desktop_banner | img_url: 'master' }}"
        alt="{{ blog.title }} banner desktop"
      />
    </div>
  {% endif %}

  {% if blog.metafields.custom.mobile_banner %}
    <div class="blog-banner mobile-banner">
      <img
        src="{{ blog.metafields.custom.mobile_banner | img_url: 'master' }}"
        alt="{{ blog.title }} banner mobile"
      />
    </div>
  {% endif %}
{% endif %}
