settings.liquid:
{% if collection and collection.title == "Pre Launch" or collection and collection.title == "Surprise Drop" or collection.title == "Back in Stock" or collection.title == "Women Airy Linen 2" or collection.title == "Seasons Best Sellers" %}

{% if collection.metafields.custom.collection_banner or collection.metafields.custom.collection_banner_mobile %}
  <style>
    .collection-banner {
      width: 100%;
      overflow: hidden;
    }

    .collection-banner img {
      width: 100%;
      height: auto;
      display: block;
    }

    .collection-banner a {
      display: block;
      width: 100%;
      height: 100%;
    }

    @media screen and (min-width: 1024px) {
      .collection-banner.desktop-banner {
        display: block;
        height: auto;
      }
      .collection-banner.mobile-banner {
        display: none;
      }
      .collection-banner.desktop-banner img {
        height: 100%;
        object-fit: cover;
      }
    }
    @media screen and (max-width: 1023px) {
      .collection-banner.desktop-banner {
        display: none;
      }
      .collection-banner.mobile-banner {
        display: block;
        height: auto;
      }
      .collection-banner.mobile-banner img {
        height: 100%;
        object-fit: cover;
      }
    }
  </style>

  {% assign banner_link = collection.metafields.custom.banner_link_text %}

  {% if collection.metafields.custom.collection_banner %}
    <div class="collection-banner desktop-banner">
      {% if banner_link != blank %}
        <a href="{{ banner_link }}">
          <img 
            src="{{ collection.metafields.custom.collection_banner | img_url: 'master' }}" 
            alt="{{ collection.title }} banner desktop"
          />
        </a>
      {% else %}
        <img 
          src="{{ collection.metafields.custom.collection_banner | img_url: 'master' }}" 
          alt="{{ collection.title }} banner desktop"
        />
      {% endif %}
    </div>
  {% endif %}

  {% if collection.metafields.custom.collection_banner_mobile %}
    <div class="collection-banner mobile-banner">
      {% if banner_link != blank %}
        <a href="{{ banner_link }}">
          <img 
            src="{{ collection.metafields.custom.collection_banner_mobile | img_url: 'master' }}" 
            alt="{{ collection.title }} banner mobile"
          />
        </a>
      {% else %}
        <img 
          src="{{ collection.metafields.custom.collection_banner_mobile | img_url: 'master' }}" 
          alt="{{ collection.title }} banner mobile"
        />
      {% endif %}
    </div>
  {% endif %}
{% endif %}

{% endif %}

{% if collection and collection.title == "Pre Launch" %}

<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>Offers Ends In – Countdown</title>
  <style>
    :root {
      --green: #18a058;
      --green-10: rgba(24, 160, 88, 0.1);
      --text: #111827; /* zinc-900 */
      --muted: #6b7280; /* gray-500 */
      --dash: #a7f3d0; /* emerald-200 */
    }

    .countdown-wrap {
      display: inline-flex; align-items: center; gap: 20px;
      border-radius: 14px; padding: 10px 14px;
      padding-bottom: 0px;
    }

    .label-pill {
      display: inline-flex; align-items: center; gap: 8px;
      padding: 8px 12px; border-radius: 10px; font-weight: 700; font-size: 14px; letter-spacing: .3px;
      color: var(--green); background: var(--green-10);
      border: 2px dashed var(--dash);
    }

    .label-pill svg { flex: 0 0 auto; width: 16px; height: 16px; }

    .timer {
      display: inline-flex; align-items: center; gap: 6px; font-variant-numeric: tabular-nums; letter-spacing: .5px;
      font-weight: 700; color: #111827;
    }

    .seg { display: inline-flex; align-items: baseline; gap: 6px; }
    .num { font-size: 20px; min-width: 2ch; text-align: right; }
    .unit { font-size: 12px; color: var(--muted); text-transform: lowercase; }

    .colon { color: #e5e7eb; font-weight: 600; }

    .expired { color: #ef4444; font-weight: 700; }

    @media (max-width: 420px) {
      .countdown-wrap { gap: 12px;}
      .num { font-size: 18px; }
      .unit { font-size: 11px; }
      .label-pill { font-size: 9px; padding: 6px 8px; }
    }
  </style>
</head>
<body>

  <div class="countdown-wrap" id="offerCountdown">
    <span class="label-pill" aria-hidden="true">
      <!-- lightning icon -->
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" aria-hidden="true">
        <path d="M13 3 L4 14 h7 l-1 7 9-11 h-7 l1-7z"/>
      </svg>
      OFFERS ENDS IN
    </span>

    <div class="timer" aria-live="polite" aria-atomic="true">
      <div class="seg"><span class="num" id="days">00</span><span class="unit">d</span></div>
      <span class="colon">·</span>
      <div class="seg"><span class="num" id="hours">00</span><span class="unit">h</span></div>
      <span class="colon">·</span>
      <div class="seg"><span class="num" id="mins">00</span><span class="unit">m</span></div>
      <span class="colon">·</span>
      <div class="seg"><span class="num" id="secs">00</span><span class="unit">s</span></div>
    </div>
  </div>

<script>
  (function () {
    // Target window in IST (UTC+05:30):
    // Start:  2025-10-02 10:00 IST  -> 04:30 UTC
    // End:    2025-10-05 10:00 IST  -> 04:30 UTC
    // Date.UTC: month is 0-based (Oct = 9)
   const startUTCms = Date.UTC(2025, 9, 10, 12, 30);
    const endUTCms   = Date.UTC(2025, 9, 12, 18, 30);

    const el = {
      days:  document.getElementById('days'),
      hours: document.getElementById('hours'),
      mins:  document.getElementById('mins'),
      secs:  document.getElementById('secs'),
      timerWrap: document.querySelector('#offerCountdown .timer')
    };

    const pad2 = n => String(Math.max(0, n)).padStart(2, '0');

    function render(diffMs) {
      if (diffMs <= 0) {
        // show 00s once expired, then replace with "Expired"
        el.days.textContent  = '00';
        el.hours.textContent = '00';
        el.mins.textContent  = '00';
        el.secs.textContent  = '00';
        if (el.timerWrap) el.timerWrap.innerHTML = '<span class="expired">Expired</span>';
        return;
      }

      const total = Math.floor(diffMs / 1000);
      const d = Math.floor(total / 86400);
      const h = Math.floor((total % 86400) / 3600);
      const m = Math.floor((total % 3600)  / 60);
      const s = total % 60;

      el.days.textContent  = pad2(d);
      el.hours.textContent = pad2(h);
      el.mins.textContent  = pad2(m);
      el.secs.textContent  = pad2(s);
    }

    function tick() {
      const nowUTCms = Date.now(); // epoch ms (UTC)
      let diff;
      if (nowUTCms < startUTCms) {
        // before start → show full window length
        diff = endUTCms - startUTCms;
      } else if (nowUTCms <= endUTCms) {
        // during offer → count down to end
        diff = endUTCms - nowUTCms;
      } else {
        // after end
        diff = 0;
        clearInterval(int);
      }
      render(diff);
    }

    tick();
    const int = setInterval(tick, 1000);
  })();
</script>

</body>
</html>
{% endif %}
