settings.liquid:
{% assign skip_policy = false %}

{%- comment -%} Template-based skip {%- endcomment -%}
{% if product.template_suffix contains 'tote-bag-addtocart' or product.template_suffix contains 'goodbye-edition' %}
  {% assign skip_policy = true %}
{% endif %}

{%- comment -%} ✅ Tag-based skip for Kurta Set {%- endcomment -%}
{% for tag in product.tags %}
  {% assign tag_lc = tag | downcase %}
  {% if tag_lc contains 'kurta set' %}
    {% assign skip_policy = true %}
    {% break %}
  {% endif %}
{% endfor %}

{% unless skip_policy %}



<!-- Meesho-style Return Policy Section -->
