{%- comment -%} ===== Values for BEST PRICE (used below) ===== {%- endcomment -%}
{% assign price_short = 1349 %}
{% assign price_long  = 1499 %}
{% assign short_mrp = price_short | times: 2 %}
{% assign long_mrp  = price_long  | times: 2 %}

<!-- Overlay Modal 3: Price (updated to Kurta Set best price; smooth line) -->
<div id="priceModal" class="modal-overlay">
  <div class="modal-box">
    <div class="modal-header">
      <span class="modal-title">BEST PRICE</span>
      <button class="modal-close">&times;</button>
    </div>
    <div class="modal-body">

      <!-- 1: Short Kurta Set (Launch Best Price) -->
      <div class="modal-step">
        <div class="step-icon-wrap">
          <div class="step-dot"></div>
          <!-- smooth animated connector -->
          <div class="step-line"></div>
        </div>
        <div>
          <div class="step-title">
            Short Kurta Set –

            <span class="now-price">₹{{ price_short }}</span>
             <span class="strike-red">₹{{ short_mrp }}</span>
          </div>
          <div class="step-desc">
            Exclusive introductory price — thoughtfully lower than any running discounts or coupon offers.<br>
            <strong style="color:#11775a;">Enjoy our best value on Saadaa sets.</strong>
          </div>
        </div>
        <img 
          src="https://cdn.shopify.com/s/files/1/0450/3476/6485/files/Rectangle_6.png?v=1755496755"
          alt="Short Kurta Set"
          style="width:83px;height:auto;display:block;margin-left:auto;flex:0 0 83px;">
      </div>

      <!-- 2: Long Kurta Set (Launch Best Price) -->
      <div class="modal-step">
        <div class="step-icon-wrap">
          <div class="step-dot"></div>
          <div class="step-line last"></div>
        </div>
        <div>
          <div class="step-title">
            Long Kurta Set –

            <span class="now-price">₹{{ price_long }}</span>
            <span class="strike-red">₹{{ long_mrp }}</span>
          </div>
          <div class="step-desc">
            Special launch price designed to offer more value than current coupons or sitewide offers.<br>
            <strong style="color:#11775a;">Our best price — no codes required.</strong>
          </div>
        </div>
        <img 
          src="https://cdn.shopify.com/s/files/1/0450/3476/6485/files/Rectangle_8.png?v=1755496755"
          alt="Long Kurta Set"
          style="width:83px;height:auto;display:block;margin-left:auto;flex:0 0 83px;">
      </div>

    </div>
    <button class="modal-continue">Continue Shopping</button>
  </div>
</div>


