/* (keep your existing CSS exactly as-is) */

/* --- Progressive step fill + icon tint + card glow --- */

/* default (inactive) */
.modal-step .step-dot,
.modal-step .step-line { background: #DADADA; }
.modal-step { transition: background 240ms ease, box-shadow 240ms ease; }
.modal-step .step-title,
.modal-step .step-desc { transition: color 240ms ease; }
.modal-step .policy-icon img,
.modal-step > img { filter: grayscale(1) brightness(.9); transition: filter 240ms ease; }

.modal-step.is-active .step-title { color: #000; }
.modal-step.is-active .step-desc  { color: #3d6057; }
.modal-step.is-active .policy-icon img,
.modal-step.is-active > img { filter: grayscale(0) brightness(1); }

/* dot/line “fill” */
.modal-step .step-dot {
  position: relative;
  width: 12px; height: 12px; border-radius: 50%;
}
.modal-step .step-dot::after {
  content: '';
  position: absolute; inset: 0;
  border-radius: inherit;
  box-shadow: 0 0 0 0 rgba(17,119,90,0.35);
  opacity: 0; pointer-events: none;
  transition: opacity 0ms 0ms;
}

/* line starts collapsed; grows when .is-filled-line is added */
.modal-step .step-line { width: 2px; height: 0; }
.modal-step.is-filled-line .step-line {
  height: 67px;
  background: #11775a;
  transition: height 600ms cubic-bezier(.4,0,.2,1);
}

/* last line stays 0 (your markup already uses .last) */
.modal-step .step-line.last { height: 0 !important; }

/* dot fill when .is-filled-dot is added */
.modal-step.is-filled-dot .step-dot {
  background: #11775a;
  transition: background 260ms ease-out;
}

/* subtle pulse when dot completes */
.modal-step.is-pulse .step-dot::after {
  opacity: 1;
  animation: pulseRing .8s ease-out forwards;
}
@keyframes pulseRing {
  0%   { box-shadow: 0 0 0 0 rgba(17,119,90,0.35); }
  100% { box-shadow: 0 0 0 12px rgba(17,119,90,0); }
}

/* reduced motion */
@media (prefers-reduced-motion: reduce) {
  .modal-step, .modal-step * { transition: none !important; animation: none !important; }
  .modal-step.is-filled-line .step-line { height: 65px; }
}

/* Coupons */
.coupon-code { color: #FF0000; font-weight: 600; }
.copy-code { cursor: pointer; }

/* Layout */
.meesho-return-policy-wrap {
  max-width: 430px;
  margin: 12px auto;
}
.policy-row {
  position: relative;
  display: flex;
  background: #F6F31; /* (kept as in your code) */
  border-radius: 8px;
  box-shadow: 0 1px 6px rgba(0,0,0,0.07);
  overflow: hidden;
  z-index: 1;
}

/* ===== RUNNING CONIC-GRADIENT BORDER (UPDATED) ===== */
@property --angle { syntax: "<angle>"; initial-value: 0deg; inherits: false; }
.policy-row::before {
  content: ""; position: absolute; inset: 0; z-index: 2; border-radius: 8px; padding: 1px;
  background: conic-gradient(from var(--angle), #d53e33 0deg 90deg, #fbb300 90deg 180deg, #377af5 180deg 270deg, #399953 270deg 360deg);
  -webkit-mask: linear-gradient(#000 0 0) content-box, linear-gradient(#000 0 0);
  -webkit-mask-composite: xor; mask-composite: exclude; pointer-events: none;
  animation: ring-spin 6s linear infinite;
}
@keyframes ring-spin { to { --angle: 360deg; } }
.policy-row > * { position: relative; z-index: 3; }

.policy-btn { flex: 1; border: none; outline: none; background: none; padding: 7px 8px; cursor: pointer; display: grid; align-items: center; gap: 0; justify-content: center; }
.policy-btn:active { background: #fee590; }
.policy-divider { width: 0.2px; background: #a2aaaa; margin: 14px 0; height: 29px; }
.policy-icon { margin-bottom: 4px; display: flex; align-items: center; justify-content: center; }
.policy-icon img { width: 100%; height: 26px; object-fit: contain; display: block; }
.policy-label { font-weight: 600; font-size: 8.5px; line-height: 13px; text-align: center; }
.return, .cash, .price { color: #000; }

.modal-overlay { position: fixed; left: 0; top: 0; bottom: 0; width: 100vw; height: 100%; background: rgba(0,0,0,.38); z-index: 12000; display: flex; align-items: flex-end; justify-content: center; opacity: 0; pointer-events: none; transition: opacity .3s; }
.modal-box { width: 100vw; max-width: 420px; background: #fff; border-radius: 22px 22px 0 0; box-shadow: 0 2px 24px rgba(0,0,0,.10); padding: 28px 20px 20px; transform: translateY(100%); transition: transform .35s cubic-bezier(.6,-0.01,0,1.01); margin-bottom: 0; }
.modal-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 30px; }
.modal-title { font-weight: 600; font-size: 0.9rem; }
.modal-close { background: none; border: none; font-size: 1.7rem; line-height: 1; color: #aaa; cursor: pointer; font-weight: 700; }
.modal-body { margin-bottom: 30px; }
.modal-step { display: flex; align-items: flex-start; }
.modal-step:last-child { margin-bottom: 0; }
.step-icon-wrap { display: flex; flex-direction: column; align-items: center; margin-right: 13px; }
.step-dot { width: 12px; height: 12px; border-radius: 50%; background: #11775a; }
.step-line { width: 2px; height: 65px; background: #11775a; }
.step-line.last { height: 0; background: transparent; }
.step-title { position: relative; font-weight: 600; font-size: 0.81rem; bottom: 6px; text-align: left; padding-right: 25px; }
.step-desc { font-size: 13px; color: #757575; margin-top: 2px; text-align: left; }
.modal-continue { width: 100%; background: #000; color: #fff; font-weight: 600; font-size: 1rem; padding: 7px 0; border-radius: 12px; cursor: pointer; box-shadow: 0 1px 6px rgba(240, 62, 108, 0.05); transition: background .2s; }
.modal-continue:active { background: #000; }
</style>

<script>
  const wait = (ms) => new Promise(r => setTimeout(r, ms));

  async function playSteps(modalEl) {
    const steps = [...modalEl.querySelectorAll('.modal-step')];
    if (!steps.length) return;

    steps.forEach(s => s.classList.remove('is-active','is-filled-dot','is-filled-line','is-pulse'));

    const dotFill = 260, pulseLag = 80, lineGrow = 600, between = 120;

    steps[0].classList.add('is-active','is-filled-dot');
    await wait(dotFill + pulseLag);
    steps[0].classList.add('is-pulse');
    await wait(300);

    if (steps[1]) {
      steps[0].classList.add('is-filled-line');
      await wait(lineGrow + between);
      steps[1].classList.add('is-active','is-filled-dot');
      await wait(dotFill + pulseLag);
      steps[1].classList.add('is-pulse');
      await wait(300);
    }

    if (steps[2]) {
      steps[1].classList.add('is-filled-line');
      await wait(lineGrow + between);
      steps[2].classList.add('is-active','is-filled-dot');
      await wait(dotFill + pulseLag);
      steps[2].classList.add('is-pulse');
    }
  }

  function openModal(modalEl) {
    modalEl.style.opacity = '1';
    modalEl.style.pointerEvents = 'auto';
    document.body.style.overflow = 'hidden';

    const box = modalEl.querySelector('.modal-box');
    void box.offsetWidth;
    box.style.transform = 'translateY(0)';

    playSteps(modalEl);
  }

  function closeModal(modalEl) {
    const box = modalEl.querySelector('.modal-box');
    box.style.transform = 'translateY(100%)';

    modalEl.querySelectorAll('.modal-step').forEach(s => {
      s.classList.remove('is-active','is-filled-dot','is-filled-line','is-pulse');
    });

    setTimeout(function () {
      modalEl.style.opacity = '0';
      modalEl.style.pointerEvents = 'none';
      document.body.style.overflow = '';
    }, 320);
  }

  document.querySelectorAll('.openReturnModalBtn').forEach(function(btn){
    btn.addEventListener('click', function(){
      const targetSel = btn.getAttribute('data-modal');
      const target = targetSel ? document.querySelector(targetSel) : null;
      if (target) openModal(target);
    });
  });

  document.querySelectorAll('.modal-overlay').forEach(function(modalEl){
    const closeBtn = modalEl.querySelector('.modal-close');
    const continueBtn = modalEl.querySelector('.modal-continue');

    if (closeBtn) closeBtn.addEventListener('click', function(){ closeModal(modalEl); });
    if (continueBtn) continueBtn.addEventListener('click', function(){ closeModal(modalEl); });

    modalEl.addEventListener('click', function(e){
      if (e.target === modalEl) closeModal(modalEl);
    });
  });

  document.addEventListener('keydown', function(e){
    if (e.key === 'Escape') {
      document.querySelectorAll('.modal-overlay').forEach(function(modalEl){
        if (getComputedStyle(modalEl).pointerEvents === 'auto') closeModal(modalEl);
      });
    }
  });

  document.querySelectorAll('.copy-code').forEach(function(el){
    el.addEventListener('click', function(){
      var code = el.getAttribute('data-code') || el.textContent.trim();
      if (navigator.clipboard && navigator.clipboard.writeText) {
        navigator.clipboard.writeText(code).then(function(){
          var prev = el.textContent;
          el.textContent = 'Copied!';
          setTimeout(function(){ el.textContent = code; }, 1200);
        }).catch(function(){ /* noop */ });
      }
    });
  });
</script>

{% endunless %}
