<style>
  /* --- WRAPPER & GRID --- */
  .saadaa-wrapper {
    width: 100%;
    padding: 10px; 
    box-sizing: border-box;
    background: #fff;
    position: relative;
    z-index: 1; /* Ensures the whole section sits properly in page flow */
  }

  .saadaa-grid {
    display: flex; /* Always flex, never column */
    flex-direction: row; 
    width: 100%;
    /* Desktop Height */
    height: 700px; 
    gap: 10px; /* Grid gap thickness */
    background: #fff; 
  }

  /* --- COLUMNS --- */
  .grid-column {
    display: flex;
    flex-direction: column;
    width: 50%; /* Always 50% width */
    gap: 10px;
  }

  /* --- BLOCKS & VIDEO --- */
  .grid-block {
    position: relative;
    width: 100%;
    overflow: hidden;
    background: #f0f0f0;
    /* FIX: Creates a new stacking context. 
       Prevents blend-mode text from bleeding into the header */
    isolation: isolate; 
  }

  .grid-block video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
    position: absolute;
    top: 0; left: 0;
    z-index: 1; /* Video at level 1 */
  }

  /* --- SIZING RATIOS (Preserved from your code) --- */
  /* Left Column Split */
  .col-left .block-top { flex: 0.7; }    /* Taller top */
  .col-left .block-bottom { flex: 0.8; } /* Shorter bottom */

  /* Right Column Split */
  .col-right .block-top { flex: 0.5; }   /* Short top */
  .col-right .block-bottom { flex: 1; } /* Tall bottom */

  /* --- TEXT OVERLAY --- */
  .overlay-text-container {
    position: absolute;
    top: 50%;        /* Center vertically in the block */
    left: 10%;       /* Position from left */
    transform: translateY(-50%);
    /* FIX: Lowered Z-Index so it stays under site header */
    z-index: 2;      
    display: flex;
    align-items: center;
    color: white;    /* Base color */
    mix-blend-mode: difference; /* Ensures visibility on any video */
    pointer-events: none; /* Allows clicks to pass through text if needed */
  }

  .text-border-left {
    height: 60px;
    width: 2px;
    background-color: currentColor;
    margin-right: 15px;
  }

  .text-group {
    display: flex;
    align-items: center;
    font-family: 'Helvetica', 'Arial', sans-serif;
  }

  .text-stack {
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-right: 12px;
    text-align: left;
  }

  /* Desktop Font Sizes */
  .text-stack span {
    font-size: 18px;
    line-height: 1.1;
    letter-spacing: 0.5px;
  }

  .text-number {
    font-size: 90px;
    font-weight: 300;
    line-height: 0.8;
  }

  /* --- MOBILE ADJUSTMENTS --- */
  /* Keep layout the same, but shrink height and text */
  @media (max-width: 768px) {
    .saadaa-grid {
      height: 400px; /* Reduce height on mobile so it fits the screen */
      gap: 5px;      /* Smaller gap on mobile */
    }

    .grid-column {
      gap: 5px;
    }

    /* Scale down text */
    .text-border-left { height: 50px; margin-right: 10px; width: 1.5px; }
    .text-stack span { font-size: 15px; }
    .text-number { font-size: 50px; }
  }
</style>

<div class="saadaa-wrapper">
  <div class="saadaa-grid">

    <div class="grid-column col-left">
      <div class="grid-block block-top">
        {% if video_1_url != blank %}
          <video autoplay loop muted playsinline>
            <source src="{{ video_1_url }}" type="video/mp4">
          </video>
        {% endif %}

        <div class="overlay-text-container">
