<script>
  function toggleBio(trigger) {
    // Find the parent paragraph
    var bioPara = trigger.parentElement;

    // Find elements
    var dots = bioPara.querySelector('.dots');
    var hiddenContent = bioPara.querySelector('.hidden-content');

    // Toggle logic
    if (hiddenContent.style.display === "inline") {
      // Collapse (Not really needed, but kept for logic safety)
      hiddenContent.style.display = "none";
      dots.style.display = "inline";
      trigger.style.display = "inline";
    } else {
      // Expand
      hiddenContent.style.display = "inline";
      dots.style.display = "none";
      trigger.style.display = "none"; // Hide the button after clicking
    }
  }
</script>

