<style>
  /* --- LAYOUT & CONTAINER --- */
  .saadaa-bundle-wrapper {
    width: 100%; max-width: 1400px; margin: 0 auto; padding: 0px 22px;
    font-family: 'Helvetica', 'Arial', sans-serif; box-sizing: border-box;
  }
  .bundle-header { margin-bottom: 20px; font-size: 20px; font-weight: 600; text-align: left; }
  /* --- CAROUSEL --- */
  .bundle-carousel {
    display: flex; overflow-x: auto; gap: 15px; padding-bottom: 12px;
    scrollbar-width: none; -ms-overflow-style: none; margin-bottom: 20px;
    align-items: stretch;
  }
  .bundle-carousel::-webkit-scrollbar { display: none; }
  /* --- PRODUCT CARD --- */
  .bundle-card { flex: 0 0 220px; display: flex; flex-direction: column; position: relative; height: 100%; }
  /* --- CLICKABLE IMAGE --- */
  .bundle-img-wrap {
    width: 100%; aspect-ratio: 3/4; background-color: #f4f4f4;
    position: relative; overflow: hidden; cursor: pointer;
  }
  .bundle-img { width: 100%; height: 100%; object-fit: cover; transition: transform 0.3s ease; }
  .bundle-img-wrap:hover .bundle-img { transform: scale(1.03); }
  .bundle-img-wrap::after {
    content: "View Highlights"; position: absolute; bottom: 0; left: 0; width: 100%;
    background: rgba(0,0,0,0.6); color: #fff; font-size: 11px; text-align: center;
    padding: 6px 0; opacity: 0; transition: opacity 0.3s;
  }
  .bundle-img-wrap:hover::after { opacity: 1; }
  /* --- PRODUCT TITLE --- */
  .bundle-prod-title {
    font-size: 13px; font-weight: 500; color: #333; margin: 12px 0 8px 0;
    display: -webkit-box; -webkit-line-clamp: 2; -webkit-box-orient: vertical;
    overflow: hidden; white-space: normal; line-height: 1.4; height: 38px;
  }
  .bundle-footer { position: relative; margin-top: auto; width: 100%; }
  /* --- SIZE BUTTONS --- */
  .select-size-btn {
    width: 100%; padding: 8px; background: #fff; border: 1px solid #e0e0e0;
    color: #000; font-size: 12px; font-weight: 600; text-transform: uppercase;
    cursor: pointer; text-align: center; transition: 0.2s; letter-spacing: 0.5px;
  }
  .select-size-btn.selected { background: #000; color: #fff; border-color: #000; }
  /* --- POPUPS --- */
  .size-popup {
    position: absolute; bottom: 100%; left: 0; width: 100%;
    background: #fff; border: 1px solid #e0e0e0; border-bottom: none;
    box-shadow: 0 -4px 15px rgba(0, 0, 0, 0.05); padding: 10px;
    box-sizing: border-box; z-index: 20; display: none;
  }
  .size-popup.active { display: block; }
  .popup-header {
    display: flex; justify-content: space-between; align-items: center;
    margin-bottom: 8px; font-size: 11px; font-weight: 600;
    text-transform: uppercase; color: #666;
  }
  .size-grid { display: grid; grid-template-columns: repeat(4, 1fr); gap: 6px; }
  .size-option {
    border: 1px solid #ddd; padding: 6px 0; text-align: center;
    cursor: pointer; transition: 0.2s; background: #fff;
  }
  .size-option:hover { border-color: #000; }
  .size-option.active-size { background-color: #fff9c4; border-color: #000; }
  .size-option.active-size .size-name { font-weight: 700; }
  .size-name { display: block; font-size: 11px; font-weight: 600; color: #000; }
  .size-measurement { display: block; font-size: 8px; color: #666; margin-top: 2px; }
  /* --- ACTION BAR --- */
  .bundle-action-bar {
    width: 100%; max-width: 600px; margin: 0 auto; background: #050505;
    color: #fff; padding: 12px 20px; border-radius: 4px; display: flex;
    justify-content: space-between; align-items: center;
  }
  .bundle-total { font-size: 18px; font-weight: 500; }
  .bundle-add-btn {
    background: transparent; border: none; color: #fff; font-size: 14px;
    font-weight: 600; cursor: pointer; opacity: 0.5; pointer-events: none;
    text-transform: uppercase;
  }
  .bundle-add-btn.active { opacity: 1; pointer-events: all; }
  .loading-text { text-align: center; padding: 40px; color: #999; }
  /* --- HIGHLIGHTS MODAL --- */
  .highlights-modal-overlay {
    display: none; position: fixed; top: 0; left: 0; width: 100%; height: 100%;
    background: rgba(0,0,0,0.6); z-index: 9999; justify-content: center;
    align-items: center; padding: 20px; box-sizing: border-box; backdrop-filter: blur(2px);
  }
  .highlights-modal-overlay.active { display: flex; }
  .highlights-content {
    background: #fff; width: 100%; max-width: 450px; padding: 17px;
    border-radius: 4px; position: relative; box-shadow: 0 10px 25px rgba(0,0,0,0.2);
    animation: modalFadeIn 0.3s ease;
  }
  @keyframes modalFadeIn {
    from { transform: translateY(10px); opacity: 0; }
    to { transform: translateY(0); opacity: 1; }
  }
  .highlights-close-btn {
    position: absolute; top: 15px; right: 20px; font-size: 24px;
    cursor: pointer; color: #999; line-height: 1;
  }
  .highlights-close-btn:hover { color: #000; }
  .h-title {
    font-size: 20px; font-weight: 700; margin-bottom: 25px;
    color: #333; border-bottom: 1px solid #eee; padding-bottom: 10px;
  }
  .h-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 20px 30px; }
  .h-item { display: flex; flex-direction: column; }
  .h-key { font-size: 14px; font-weight: 500; color: #8c9bad; margin-bottom: 5px; }
  .h-value { font-size: 13px; color: #333; line-height: 1.4; }
  .h-disclaimer {
    margin-top: 30px; padding-top: 15px; border-top: 1px solid #eee;
    font-size: 11px; color: #333; font-weight: 600; line-height: 1.4;
  }
  @media (min-width:780px) {
    .bundle-header { font-size: 26px; text-align: center; margin-top: 35px; }
    .bundle-carousel { justify-content: center; margin-bottom: 40px; }
  }
</style>
<div class="saadaa-bundle-wrapper" id="saadaa-wardrobe">
  <div class="bundle-header">Her SAADAA Wardrobe</div>
  <div id="saadaa-carousel" class="bundle-carousel">
    <div class="loading-text">Loading Wardrobe...</div>
  </div>
  <div class="bundle-action-bar">
    <div id="bundle-total-price" class="bundle-total">Rs 0</div>
    <button id="bundle-add-btn" class="bundle-add-btn">Select Size For All Items</button>
  </div>
</div>
<div id="highlights-modal" class="highlights-modal-overlay" onclick="closeHighlights(event)">
  <div class="highlights-content" onclick="event.stopPropagation()">
    <span class="highlights-close-btn" onclick="closeHighlights(event)">&times;</span>
    <div class="h-title">Key Highlights</div>
    <div id="highlights-grid" class="h-grid">
      </div>
    <div class="h-disclaimer">
      Disclaimer: There might be a slight variation in the actual color of the product due to different screen resolutions.
    </div>
  </div>
</div>
<script>
(function() {
  const CONFIG = {
    endpoint: "https://saadaa-design.myshopify.com/api/2024-07/graphql.json",
    token: "388bd37219d8ab7daee1c58a23c03cc8",
    searchTag: "dany-page"
  };
  /* --- 1. MANUAL HIGHLIGHTS DATA (BY INDEX) --- */
  const MANUAL_HIGHLIGHTS_BY_INDEX = {
    0: [
      { key: "Fabric", value: "100% Cotton" },
      { key: "Fit", value: "Regular" },
      { key: "Rise", value: "Mid Rise" },
      { key: "Waistband", value: "Half-elasticated" },
      { key: "Occasion", value: "Formal, Casual & Ethnic" },
      { key: "Washcare", value: "Machine Wash In Cold Water" }
    ],
    1: [
      { key: "Fabric", value: "85% Cotton 15% Linen" },
      { key: "Fit", value: "Regular" },
      { key: "Neckline", value: "Mandarin collar" },
      { key: "Sleeves", value: "3/4th" },
      { key: "Occasion", value: "Formal, Casual & Ethnic" },
      { key: "Washcare", value: "Machine Wash In Cold Water" }
    ],
     2: [
      { key: "Fabric", value: "100% Viscose - Plant based fabric" },
      { key: "Fit", value: "Regular" },
      { key: "Neckline", value: "V-neckline with Mandarin collar" },
      { key: "Sleeves", value: "3/4th" },
      { key: "Occasion", value: "Formal, Semi-formal & Casual" },
      { key: "Washcare", value: "Machine Wash In Cold Water" }
    ],
         3: [
      { key: "Fabric", value: "100% Viscose Vortex" },
      { key: "Fit", value: "Regular" },
      { key: "Rise", value: "Mid Rise" },
      { key: "Waistband", value: "Half-elasticated" },
      { key: "Occasion", value: "Formal, Casual & Ethnic" },
      { key: "Washcare", value: "Machine Wash In Cold Water" }
    ],
         4: [
      { key: "Fabric", value: "85% Cotton 15% Linen" },
      { key: "Fit", value: "Flared Fit" },
      { key: "Stitch", value: "Durable Overlock Stitches" },
      { key: "Belt", value: "Adjustable Belt" },
      { key: "Occasion", value: "Casual, Day wear" },
      { key: "Washcare", value: "Machine Wash In Cold Water" }
    ],
    // Add indices 2, 3, 4 etc. as needed
  };
  /* --- 2. MANUAL MEASUREMENTS DATA (BY INDEX) --- */
  const MANUAL_MEASUREMENTS_BY_INDEX = {
    0: { 
      "S": "31\" - 32\"", 
      "M": "33\" - 34\"", 
      "L": "35\" - 36\"", 
      "XL": "37\" - 38\"",
      "2XL": "39\" - 41\"",
      "3XL": "42\" - 44\"",
      "4XL": "45\" - 47\""
    }, 
    1: {
      "S": "35\" - 36\"", 
      "M": "37\" - 38\"", 
      "L": "39\" - 40\"", 
      "XL": "41\" - 42\"",
      "2XL": "43\" - 44\"",
      "3XL": "45\" - 46\"",
      "4XL": "47\" - 48\""
    },
    // Fallback if specific index is missing
    2: { 
      "XS": "33\" - 34\"",
      "S": "35\" - 36\"", 
      "M": "37\" - 38\"", 
      "L": "39\" - 40\"", 
      "XL": "41\" - 42\"",
      "2XL": "43\" - 44\"",
      "3XL": "45\" - 46\"",
      "4XL": "47\" - 48\""
    },
        3: { 
      "XS": "28\" - 30\"",
      "S": "31\" - 32\"", 
      "M": "33\" - 34\"", 
      "L": "35\" - 36\"", 
      "XL": "37\" - 38\"",
      "2XL": "39\" - 41\"",
      "3XL": "42\" - 44\"",
      "4XL": "45\" - 47\""
    },
            4: { 
      "XS": "33\" - 34\"",
      "S": "35\" - 36\"", 
      "M": "37\" - 38\"", 
      "L": "39\" - 40\"", 
      "XL": "41\" - 42\"",
      "2XL": "43\" - 44\"",
      "3XL": "45\" - 46\"",
      "4XL": "47\" - 48\""
    },
  };
  let products = [];
  let selections = {};

  const carouselEl = document.getElementById('saadaa-carousel');
  const totalEl = document.getElementById('bundle-total-price');
  const addBtn = document.getElementById('bundle-add-btn');
  const modalEl = document.getElementById('highlights-modal');
  const modalGridEl = document.getElementById('highlights-grid');
  async function fetchProducts() {
    const query = `{
      products(first: 5, query: "tag:${CONFIG.searchTag}") {
        edges {
          node {
            id
            title
            handle
            featuredImage { url(transform: {maxWidth: 500}) }
            priceRange { minVariantPrice { amount } }
            variants(first: 50) {
              edges {
                node { id title availableForSale }
              }
            }
          }
        }
      }
    }`;
    try {
      const response = await fetch(CONFIG.endpoint, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "X-Shopify-Storefront-Access-Token": CONFIG.token
        },
        body: JSON.stringify({ query })
      });
      const result = await response.json();
      if(result.errors) { console.error(result.errors); return; }
      products = result.data.products.edges.map(edge => edge.node);
      renderProducts();
      calculateBaseTotal();
    } catch (error) {
      console.error("Error:", error);
      carouselEl.innerHTML = '<div class="loading-text">Error loading products.</div>';
    }
  }
  function renderProducts() {
    carouselEl.innerHTML = '';
    products.forEach((product, index) => {
      const card = document.createElement('div');
      card.className = 'bundle-card';
      const imgUrl = product.featuredImage ? product.featuredImage.url : '';

      card.innerHTML = `
        <div class="bundle-img-wrap" onclick="openProductHighlights(${index})">
          <img src="${imgUrl}" alt="${product.title}" class="bundle-img">
        </div>
        <div class="bundle-prod-title">${product.title}</div>
        <div class="bundle-footer">
          <div class="size-popup" id="popup-${index}">
            <div class="popup-header"><span>SIZE:</span></div>
            <div class="size-grid" id="grid-${index}">
              ${buildDynamicSizeGrid(product, index)}
            </div>
          </div>
          <button class="select-size-btn" id="btn-${index}" onclick="togglePopup(${index})">
            SELECT SIZE
          </button>
        </div>
      `;
      carouselEl.appendChild(card);
    });
  }
  /* --- GRID BUILDER (UPDATED TO USE INDEX) --- */
  function buildDynamicSizeGrid(product, index) {
    let html = '';
    const variants = product.variants.edges;

    // Look up measurement map by Index (0, 1, 2...)
    // Fallback to 'default' if index not found
    const measureMap = MANUAL_MEASUREMENTS_BY_INDEX[index] || MANUAL_MEASUREMENTS_BY_INDEX['default'];
    variants.forEach(variantEdge => {
      const variant = variantEdge.node;
      if (variant.availableForSale) {
        const sizeLabel = variant.title; // e.g., "S", "M"

        // Get measurement for this specific Size Label from the map
        const measurement = measureMap[sizeLabel] || "";

        html += `<div class="size-option" onclick="selectSize(this, '${variant.id}', '${sizeLabel}', ${index})">
                   <span class="size-name">${sizeLabel}</span>
                   <span class="size-measurement">${measurement}</span>
                 </div>`;
      }
    });
    return html || '<div style="font-size:10px; color:red; text-align:center;">Sold Out</div>';
  }
  /* --- MANUAL HIGHLIGHTS RENDERER --- */
  window.openProductHighlights = function(index) {
    const data = MANUAL_HIGHLIGHTS_BY_INDEX[index] || [];
    if (data.length === 0) {
      modalGridEl.innerHTML = '<div style="grid-column: span 2; text-align:center; padding:20px;">No details available for this product yet.</div>';
    } else {
      let htmlContent = '';
      data.forEach(item => {
        htmlContent += `
          <div class="h-item">
            <span class="h-key">${item.key}</span>
            <span class="h-value">${item.value}</span>
          </div>
        `;
      });
      modalGridEl.innerHTML = htmlContent;
    }
    modalEl.classList.add('active');
  };
  window.closeHighlights = function(e) {
    if(e) e.preventDefault();
    modalEl.classList.remove('active');
  };
  window.togglePopup = function(index) {
    document.querySelectorAll('.size-popup').forEach((el, i) => {
      if (i !== index) el.classList.remove('active');
    });
    document.getElementById(`popup-${index}`).classList.toggle('active');
  };
  window.selectSize = function(el, variantIdRaw, sizeLabel, index) {
    const grid = document.getElementById(`grid-${index}`);
    grid.querySelectorAll('.size-option').forEach(opt => opt.classList.remove('active-size'));
    el.classList.add('active-size');
    let numericId = variantIdRaw;
    try {
        if(!variantIdRaw.includes('/')) numericId = atob(variantIdRaw);
        numericId = numericId.split('/').pop();
    } catch(e) {}

    selections[index] = numericId;
    const btn = document.getElementById(`btn-${index}`);
    btn.textContent = `SIZE: ${sizeLabel}`;
    btn.classList.add('selected');
    document.getElementById(`popup-${index}`).classList.remove('active');

    const count = Object.keys(selections).length;
    if (count === products.length) {
       addBtn.classList.add('active');
       addBtn.textContent = `ADD ${count} ITEMS TO CART`;
    }
  };
  function calculateBaseTotal() {
    let sum = 0;
    products.forEach(p => sum += parseFloat(p.priceRange.minVariantPrice.amount));
    totalEl.textContent = `Rs ${Math.round(sum)}`;
  }
  addBtn.addEventListener('click', async () => {
    if (!addBtn.classList.contains('active')) return;
    addBtn.textContent = "ADDING...";
    const itemsToAdd = Object.values(selections).map(id => ({ id: parseInt(id), quantity: 1 }));
    try {
      await fetch(window.Shopify.routes.root + 'cart/add.js', {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ items: itemsToAdd })
      });
    } catch (err) {
      console.error(err);
      addBtn.textContent = "ERROR.";
    }
  });
  fetchProducts();
})();
</script>

