<style>
  /* --- SECTION CONTAINER --- */
  .saadaa-explore-wrapper {
    width: 100%;
    max-width: 1000px; /* Limits width on desktop */
    margin: 0 auto;
    padding: 10px 20px;
    padding-bottom: 40px;
    box-sizing: border-box;
    font-family: 'Helvetica', 'Arial', sans-serif;
    text-align: center;
    color: #000;
  }

  /* --- MAIN HEADING --- */
  .explore-heading {
    font-size: 28px;
    font-weight: 500;
    margin-bottom: 35px;
  }

  /* --- PROFILE GRID --- */
  .explore-grid {
    display: grid;
    /* Default: 4 columns on desktop */
    grid-template-columns: repeat(4, 1fr); 
    gap: 30px;
    margin-bottom: 60px;
  }

  /* ✅ Whole card clickable */
  .explore-card{
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    text-decoration: none;
    color: inherit;
    cursor: pointer;
  }
  .explore-card:focus-visible{
    outline: 2px solid #000;
    outline-offset: 6px;
    border-radius: 10px;
  }

  /* Circular Image */
  .explore-img-wrap {
    width: 150px; /* Adjust circle size */
    height: 150px;
    border-radius: 50%;
    overflow: hidden;
    background-color: #f4f4f4;
    margin-bottom: 15px;
  }

  .explore-img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  /* Text Info */
  .explore-name {
    font-size: 15px;
    font-weight: 600;
    margin: 0 0 5px 0;
    color: #000;
  }

  .explore-role {
    font-size: 13px;
    color: #777;
    margin: 0 0 8px 0;
    line-height: 1.3;
    max-width: 180px; /* Prevents text from getting too wide */
  }

  .explore-link {
    font-size: 12px;
    color: #999;
    text-decoration: none;
    transition: color 0.2s;
  }
  .explore-card:hover .explore-link {
    color: #000;
  }

  /* --- FOOTER TEXT SECTION --- */
  .explore-footer-text {
    margin-bottom: 30px;
  }

  .footer-title {
    font-size: 22px;
    font-weight: 400;
    margin: 0 0 10px 0;
    color: #333;
  }

  .footer-subtitle {
    font-size: 16px;
    color: #666;
    margin: 0;
  }

  /* --- BUTTON GROUP --- */
  .explore-actions {
    display: flex;
    flex-direction: column;
    gap: 15px;
    max-width: 400px;
    margin: 0 auto;
  }

  .ex-btn {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    padding: 16px;
    text-decoration: none;
    font-size: 14px;
    font-weight: 500;
    border-radius: 6px;
    box-sizing: border-box;
    transition: 0.3s;
  }

  .ex-btn-primary {
    background-color: #050505;
    color: #fff;
    border: 1px solid #050505;
  }
  .ex-btn-primary:hover {
    opacity: 0.9;
  }

  .ex-btn-secondary {
    background-color: #fff;
    color: #000;
    border: 1px solid #e0e0e0;
  }
  .ex-btn-secondary:hover {
    border-color: #000;
  }

  /* --- MOBILE RESPONSIVENESS --- */
  @media (max-width: 768px) {
    .explore-grid {
      /* Mobile: 2 columns exactly like screenshot */
      grid-template-columns: repeat(2, 1fr); 
      gap: 30px 10px; /* Vertical gap 30, Horizontal gap 10 */
    }

    .explore-img-wrap {
      width: 130px; /* Slightly smaller circles on mobile */
      height: 130px;
    }

    .explore-heading {
      font-size: 24px;
    }
  }
</style>

