{% assign skip_policy = false %}

{%- comment -%} Template-based skip {%- endcomment -%}
{% if product.template_suffix contains 'tote-bag-addtocart' or product.template_suffix contains 'goodbye-edition' or product.template_suffix contains 'pre-launch' %}
  {% assign skip_policy = true %}
{% endif %}

