<style>
/* --- Valentine Color Palette --- */
:root {
  --val-primary: #e91e63;   /* Deep Rose */
  --val-light: #fce4ec;     /* Soft Pink */
  --val-text: #880e4f;      /* Dark Wine/Red */
  --val-bg-row: #fff0f6;    /* Very Light Pink */
  --val-border: #f8bbd0;
}

@media (min-width: 900px) {
  .meesho-return-policy-wrap,
  #returnModal { display: none !important; }
}

/* --- Layout --- */
.meesho-return-policy-wrap {
  max-width: 430px;
  margin: 12px auto;
}

.policy-row {
  position: relative;
  display: flex;
  border-radius: 12px;
  box-shadow: 0 4px 12px rgba(233, 30, 99, 0.1);
  overflow: hidden;
  z-index: 1;
}

/* --- Valentine Animated Border --- */
@property --angle {
  syntax: "<angle>";
  initial-value: 0deg;
  inherits: false;
}

.policy-row::before {
  content: "";
  position: absolute;
  inset: 0;
  z-index: 2;
  border-radius: 12px;
  padding: 1.5px;

  /* Updated Gradient: No White */
  background: conic-gradient(
    from var(--angle),
    #e91e63 0deg 120deg,    /* Deep Rose */
    #ffc107 120deg 180deg,  /* Gold */
    #f06292 180deg 360deg   /* Soft Pink */
  );

  -webkit-mask:
    linear-gradient(#000 0 0) content-box,
    linear-gradient(#000 0 0);
  -webkit-mask-composite: xor;
          mask-composite: exclude;
  pointer-events: none;
  animation: ring-spin 4s linear infinite;
}

@keyframes ring-spin {
  to { --angle: 360deg; }
}

.policy-row > * { position: relative; z-index: 3; }

.policy-btn {
  flex: 1;
  border: none;
  background: none;
  padding: 10px 8px;
  cursor: pointer;
  display: flex;
  flex-direction: column; /* Stack icon and text */
  align-items: center;
  justify-content: center;
  gap: 4px;
}

.policy-divider {
  width: 1px;
  background: #f48fb1; /* Pink Divider */
  margin: 10px 0;
  opacity: 0.5;
}

/* Icon Tinting to Red/Pink */
.policy-icon img {
  width: 24px;
  height: 24px;
  object-fit: contain;
  display: block;
  /* Filter to turn black/green icons into deep pink/red */
  filter: sepia(1) saturate(5) hue-rotate(320deg) brightness(0.9);
}

.policy-label {
  font-weight: 600;
  font-size: 10px;
  line-height: 1.2;
  text-align: center;
  color: var(--val-text); /* Wine Red Text */
  font-family: 'Segoe UI', sans-serif;
}

/* --- Modal Styles --- */
.modal-overlay {
  position: fixed;
  left: 0; top: 0; bottom: 0; width: 100vw; height: 100%;
  background: rgba(88, 14, 45, 0.4); /* Dark Red Overlay */
  z-index: 12000;
  display: flex;
  align-items: flex-end;
  justify-content: center;
  opacity: 0;
  pointer-events: none;
  transition: opacity .3s;
  backdrop-filter: blur(2px);
}

.modal-box {
  width: 100vw;
  max-width: 420px;
  background: #fff; /* Could be #fff0f6 for full pink */
  border-radius: 24px 24px 0 0;
  box-shadow: 0 -4px 24px rgba(233, 30, 99, 0.15);
  padding: 30px 24px 24px;
  transform: translateY(100%);
  transition: transform .35s cubic-bezier(.6,-0.01,0,1.01);
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
}

.modal-title {
  font-weight: 700;
  font-size: 1.1rem;
  color: var(--val-primary);
  font-family: 'Georgia', serif; /* Romantic Font */
  letter-spacing: 0.5px;
}

.modal-close {
  background: none; border: none;
  font-size: 2rem; line-height: 1;
  color: #f06292;
  cursor: pointer;
}

/* --- Timeline Steps --- */
.modal-step {
  display: flex;
  align-items: flex-start;
  margin-bottom: 2px; /* spacing controlled by line height */
}

.step-icon-wrap {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-right: 15px;
  min-width: 16px;
}

/* Default State */
.step-dot {
  width: 14px; height: 14px;
  border-radius: 50%;
  background: #e0e0e0;
  position: relative;
  transition: background 0.3s;
}

.step-line {
  width: 2px; height: 0; /* Grows via JS */
  background: var(--val-primary);
  opacity: 0.3;
}

.step-line.last { display: none; }

.step-img {
  width: 70px;
  height: auto;
  display: block;
  margin-left: auto;
  flex: 0 0 70px;
  border-radius: 8px;
}

.step-title {
    /* font-weight: 600; */
    font-size: 0.8rem;
    color: #333;
    margin-bottom: 4px;
    padding-right: 10px;
    line-height: 1.4;
    /* font-family: 'Georgia', serif; */
    text-align: left;
}

.step-desc {
    font-size: 0.85rem;
    color: #666;
    text-align: left;
}

/* Active State Logic */
.modal-step.is-active .step-dot {
  background: var(--val-primary); /* Pink Dot */
  box-shadow: 0 0 0 4px var(--val-light);
}
.modal-step.is-active .step-title {
  color: var(--val-text);
}

/* Line Animation Class */
.modal-step.is-filled-line .step-line {
  height: 70px; /* Adjust based on content height */
  opacity: 1;
  transition: height 0.6s ease;
}

/* Coupon Styles */
.coupon-code {
  background: var(--val-light);
  color: var(--val-primary);
  padding: 2px 6px;
  border: 1px dashed var(--val-primary);
  border-radius: 4px;
  font-weight: 700;
  cursor: pointer;
}

/* Continue Button */
.modal-continue {
  width: 100%;
  margin-top: 20px;
  background: linear-gradient(135deg, #ec407a, #d81b60);
  color: #fff;
  font-weight: 600;
  font-size: 1rem;
  padding: 12px 0;
  border-radius: 11px; /* Pill shape */
  border: none;
  cursor: pointer;
  box-shadow: 0 4px 14px rgba(233, 30, 99, 0.3);
  transition: transform 0.2s;
  font-family: 'Georgia', serif;
  letter-spacing: 0.5px;
}

.modal-continue:active {
  transform: scale(0.98);
}

/* Reduced Motion */
@media (prefers-reduced-motion: reduce) {
  .policy-row::before { animation: none; }
  .modal-step.is-filled-line .step-line { height: 70px; transition: none; }
}
</style>

<script>
  // Helper to wait
  const wait = (ms) => new Promise(r => setTimeout(r, ms));

  async function playSteps(modalEl) {
    const steps = [...modalEl.querySelectorAll('.modal-step')];
    if (!steps.length) return;

    // Reset classes
    steps.forEach(s => s.classList.remove('is-active', 'is-filled-line'));

    // Timings
    const dotFill = 200;
    const lineGrow = 500;
    const between = 100;

    // Step 1
    steps[0].classList.add('is-active');
    await wait(dotFill);

    // Line 1 -> Step 2
    if (steps[1]) {
      steps[0].classList.add('is-filled-line'); // Grow line down
      await wait(lineGrow + between);
      steps[1].classList.add('is-active');
      await wait(dotFill);
    }

    // Line 2 -> Step 3
    if (steps[2]) {
      steps[1].classList.add('is-filled-line');
      await wait(lineGrow + between);
      steps[2].classList.add('is-active');
    }
  }

  // Open Modal
  function openModal(modalEl) {
    modalEl.style.opacity = '1';
    modalEl.style.pointerEvents = 'auto';
    document.body.style.overflow = 'hidden';

    const box = modalEl.querySelector('.modal-box');
    void box.offsetWidth; // Trigger reflow
    box.style.transform = 'translateY(0)';

    // Play animation
    playSteps(modalEl);
  }

  // Close Modal
  function closeModal(modalEl) {
    const box = modalEl.querySelector('.modal-box');
    box.style.transform = 'translateY(100%)';

    // Reset steps for next time
    setTimeout(() => {
       modalEl.querySelectorAll('.modal-step').forEach(s => {
         s.classList.remove('is-active', 'is-filled-line');
       });
       modalEl.style.opacity = '0';
       modalEl.style.pointerEvents = 'none';
       document.body.style.overflow = '';
    }, 320);
  }

  // Event Listeners
  document.querySelectorAll('.openReturnModalBtn').forEach(btn => {
    btn.addEventListener('click', function() {
      const target = document.querySelector(btn.getAttribute('data-modal'));
      if (target) openModal(target);
    });
  });

  document.querySelectorAll('.modal-overlay').forEach(modalEl => {
    const closeBtn = modalEl.querySelector('.modal-close');
    const continueBtn = modalEl.querySelector('.modal-continue');

    if (closeBtn) closeBtn.addEventListener('click', () => closeModal(modalEl));
    if (continueBtn) continueBtn.addEventListener('click', () => closeModal(modalEl));

    modalEl.addEventListener('click', (e) => {
      if (e.target === modalEl) closeModal(modalEl);
    });
  });

  // Copy Code Logic
  document.querySelectorAll('.copy-code').forEach(el => {
    el.addEventListener('click', function() {
      const code = el.getAttribute('data-code') || el.textContent.trim();
      if (navigator.clipboard && navigator.clipboard.writeText) {
        navigator.clipboard.writeText(code).then(() => {
          const original = el.textContent;
          el.textContent = '❤';
          setTimeout(() => el.textContent = original, 1200);
        });
      }
    });
  });
</script>

{% endunless %}
