{%- comment -%}
  Only render Combo Selector if metafield `custom.combo_config` is attached
{%- endcomment -%}
{% assign combo_ref = product.metafields.custom.combo_config %}
{% if combo_ref and combo_ref.value %}

<!-- ================== COMBO SELECTOR (Metaobject-driven) ================== -->
<div class="combo" id="comboRoot" role="group" aria-labelledby="comboTitle">
<div class="combo-header">
  <h3 id="comboTitle">Select Combo</h3>
  <div id="KiwiSizingChart" data-display-mode="2" style="margin-top: -1px;"></div>
</div>


  <div class="row" data-index="1">
    <div class="field">
      <label>Color 1</label>
      <select class="color" required aria-label="Color 1"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 1</label>
      <select class="size" required disabled aria-label="Size 1"><option value="">Select</option></select>
    </div>
  </div>

  <div class="row" data-index="2">
    <div class="field">
      <label>Color 2</label>
      <select class="color" required aria-label="Color 2"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 2</label>
      <select class="size" required disabled aria-label="Size 2"><option value="">Select</option></select>
    </div>
  </div>

  <div class="row" data-index="3">
    <div class="field">
      <label>Color 3</label>
      <select class="color" required aria-label="Color 3"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 3</label>
      <select class="size" required disabled aria-label="Size 3"><option value="">Select</option></select>
    </div>
  </div>

  <div class="row" data-index="4">
    <div class="field">
      <label>Color 4</label>
      <select class="color" required aria-label="Color 4"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 4</label>
      <select class="size" required disabled aria-label="Size 4"><option value="">Select</option></select>
    </div>
  </div>

  <button id="comboAdd" class="btn">ADD TO CART</button>
  <p id="comboMsg" class="msg" aria-live="polite"></p>
</div>

<style>

  .combo-header {
  display: flex;
  align-items: center;
  justify-content: space-between; /* pushes them apart */
  gap: 12px; /* space between title and chart */
}

.combo-header h3 {
  margin: 0;
  font-size: 18px;
}

  .combo{border:1px solid #ececec;border-radius:10px;padding:16px}
  .combo h3{margin:0 0 10px;font-size:18px}
  .row{display:grid;grid-template-columns:1fr 1fr;gap:12px;margin-bottom:10px}
  .field{display:flex;flex-direction:column;gap:6px}
  .field label{font-size:14px}
  select{height:44px;border:1px solid #d9d9d9;border-radius:8px;padding:0 10px;background:#fff}
  .btn {
    margin-top: 20px;
    height: 48px;
    border: 0;
    border-radius: 10px;
    background: #111;
    color: #fff;
    cursor: pointer;
    padding: 0 18px;
    width: 100%;
}
  .btn[disabled]{opacity:.5;cursor:not-allowed}
  .msg{margin-top:8px;font-size:14px}
  .msg.error{color:#c62828}
  .msg.success{color:#2e7d32}


.btn.ks-chart-modal-button {
    background-color: #fff !important;
    border: none;
    bottom: 15px;
    position: relative;
}


</style>

<script id="comboConfig" type="application/json">
{
  "sizeOptionNames": ["Size","Waist Size","尺寸","Talla","Tamanho"],
  "rows": {
    "1": {{ combo_ref.value.color_size_1 | strip | json }},
    "2": {{ combo_ref.value.color_size_2 | strip | json }},
    "3": {{ combo_ref.value.color_size_3 | strip | json }},
    "4": {{ combo_ref.value.color_size_4 | strip | json }}
  }
}
</script>

<script>
/**
 * COMBO SELECTOR — Metaobject-driven
 */
const $msg = document.getElementById("comboMsg");
function setMsg(t, type=""){ if($msg){ $msg.textContent = t; $msg.className = "msg " + (type||""); } }

function fromMetaobjectJSON(){
  const el = document.getElementById("comboConfig");
  if(!el) return null;
  try { return JSON.parse(el.textContent || "null"); }
  catch(e){ console.error("[Combo] Bad comboConfig JSON", e); return null; }
}

function normalizeField(input){
  if (input == null) return "";
  if (typeof input === "string") return input;
  if (typeof input === "object" && typeof input.value === "string") return input.value;
  return String(input || "");
}

function parseColorHandleList(raw){
  let text = normalizeField(raw);
  text = text.replace(/<br\s*\/?>/gi, "\n");
  if(!text.trim()) return {};
  const tokens = text.replace(/\r?\n/g, " | ").split("|").map(t => t.trim()).filter(Boolean);
  const map = {};
  for (let i = 0; i < tokens.length; i += 2){
    const color = tokens[i], handle = tokens[i+1] || "";
    if (!color || !handle) continue;
    if (/^\d+$/.test(handle)) continue;
    map[color] = handle;
  }
  return map;
}

async function fetchProductByHandle(handle){
  const res = await fetch(`/products/${encodeURIComponent(handle)}.js`, { credentials: "same-origin" });
  if(!res.ok) throw new Error(`Failed /products/${handle}.js`);
  return res.json();
}

function detectSizeIndex(productJson, sizeOptionNames){
  const names = (productJson.options || []).map(o => (o.name || "").toLowerCase());
  const wanted = (sizeOptionNames || []).map(s => s.toLowerCase());
  for (let i=0;i<names.length;i++) if (wanted.includes(names[i])) return i+1;
  return names.length === 1 ? 1 : 2;
}

function extractAvailableSizes(productJson, sizeOptionNames){
  const idx = detectSizeIndex(productJson, sizeOptionNames);
  const key = "option" + idx;
  const sizes = [];
  for (const v of productJson.variants || []) if (v.available) sizes.push(v[key]);
  return sizes.filter((s,i,a)=>a.indexOf(s)===i);
}

function findVariantIdBySize(productJson, sizeLabel, sizeOptionNames){
  const idx = detectSizeIndex(productJson, sizeOptionNames);
  const key = "option" + idx;
  const v = (productJson.variants || []).find(x => x.available && x[key] === sizeLabel);
  return v ? v.id : null;
}

(function initCombo(){
  const meta = fromMetaobjectJSON() || {};
  const sizeOptionNames = meta.sizeOptionNames || ["Size","Waist Size","尺寸","Talla","Tamanho"];
  const rowsCfg = [];
  for (let i = 1; i <= 4; i++){
    const raw = meta.rows && meta.rows[String(i)];
    const colorsMap = parseColorHandleList(raw);
    if (Object.keys(colorsMap).length) rowsCfg.push({ colors: colorsMap });
  }
  const allRows = Array.from(document.querySelectorAll("#comboRoot .row"));
  allRows.forEach((rowEl, idx) => {
    if (idx >= rowsCfg.length) rowEl.style.display = "none";
    else {
      const rowCfg = rowsCfg[idx];
      const colorSel = rowEl.querySelector("select.color");
      const sizeSel  = rowEl.querySelector("select.size");
      colorSel.innerHTML = `<option value="">Select</option>`;
      sizeSel.innerHTML  = `<option value="">Select</option>`;
      sizeSel.disabled = true;
      Object.keys(rowCfg.colors).forEach(name=>{
        const opt=document.createElement("option"); opt.value=name; opt.textContent=name; colorSel.appendChild(opt);
      });
      colorSel.addEventListener("change", async () => {
        sizeSel.innerHTML = `<option value="">Select</option>`; sizeSel.disabled = true;
        const colorName=colorSel.value; if(!colorName) return;
        try{
          const product = await fetchProductByHandle(rowCfg.colors[colorName]);
          rowCfg.__productCache = rowCfg.__productCache || {}; rowCfg.__productCache[colorName]=product;
          const sizes=extractAvailableSizes(product,sizeOptionNames);
          sizes.forEach(s=>{const opt=document.createElement("option"); opt.value=s; opt.textContent=s; sizeSel.appendChild(opt);});
          if(sizes.length) sizeSel.disabled=false;
        }catch(e){console.error(e); setMsg(`${colorName} color is out of stock` , "error");}
      });
    }
  });

  document.getElementById("comboAdd")?.addEventListener("click", async () => {
    const items = [];
    for (let i=0;i<rowsCfg.length;i++){
      const rowEl=document.querySelector(`#comboRoot .row[data-index="${i+1}"]`);
      const colorSel=rowEl.querySelector("select.color"), sizeSel=rowEl.querySelector("select.size");
      const colorName=colorSel.value, sizeLabel=sizeSel.value;
      if(!colorName || !sizeLabel){ setMsg(`Please select Color and Size for row ${i+1}.`, "error"); return; }
      const rowCfg=rowsCfg[i]; const product=rowCfg.__productCache?.[colorName] || await fetchProductByHandle(rowCfg.colors[colorName]);
      const variantId=findVariantIdBySize(product,sizeLabel,sizeOptionNames);
      if(!variantId){ setMsg(`Selected size unavailable for ${colorName}.`, "error"); return; }
      items.push({id:variantId, quantity:1});
    }
    try{
      await fetch("/cart/add.js",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({items})});
      setMsg("Added to cart!","success");
      if(window.Gokwik?.openCart) window.Gokwik.openCart();
      else if(window.gokwik?.openCart) window.gokwik.openCart();
      else document.dispatchEvent(new CustomEvent("cart:updated",{detail:{source:"combo"}}));
    }catch(err){console.error(err); setMsg("Could not add to cart.","error");}
  });
})();
</script>

{% endif %}
