{%- comment -%}
  Only render Combo Selector if metafield `custom.combo_config` is attached
{%- endcomment -%}
{% assign combo_ref = product.metafields.custom.combo_config %}
{% if combo_ref and combo_ref.value %}

<!-- ================== COMBO SELECTOR (Metaobject-driven) ================== -->
<div class="combo" id="comboRoot" role="group" aria-labelledby="comboTitle">
<div class="combo-header">
  <h3 id="comboTitle">Select Combo</h3>
  <div id="KiwiSizingChart" data-display-mode="2" style="margin-top: -1px;"></div>
</div>

  <!-- ROW 1 -->
  <div class="row" data-index="1">
    <div class="field">
      <label>Color 1</label>
      <select class="color" required aria-label="Color 1"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 1</label>
      <select class="size" required disabled aria-label="Size 1"><option value="">Select</option></select>
    </div>
  </div>

  <!-- ROW 2 -->
  <div class="row" data-index="2">
    <div class="field">
      <label>Color 2</label>
      <select class="color" required aria-label="Color 2"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 2</label>
      <select class="size" required disabled aria-label="Size 2"><option value="">Select</option></select>
    </div>
  </div>

  <!-- ROW 3 -->
  <div class="row" data-index="3">
    <div class="field">
      <label>Color 3</label>
      <select class="color" required aria-label="Color 3"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 3</label>
      <select class="size" required disabled aria-label="Size 3"><option value="">Select</option></select>
    </div>
  </div>

  <!-- ROW 4 -->
  <div class="row" data-index="4">
    <div class="field">
      <label>Color 4</label>
      <select class="color" required aria-label="Color 4"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 4</label>
      <select class="size" required disabled aria-label="Size 4"><option value="">Select</option></select>
    </div>
  </div>

  <!-- ROW 5 (NEW) -->
  <div class="row" data-index="5">
    <div class="field">
      <label>Color 5</label>
      <select class="color" required aria-label="Color 5"><option value="">Select</option></select>
    </div>
    <div class="field">
      <label>Size 5</label>
      <select class="size" required disabled aria-label="Size 5"><option value="">Select</option></select>
    </div>
  </div>

  <button id="comboAdd" class="btn">ADD TO CART</button>
  <p id="comboMsg" class="msg" aria-live="polite"></p>
</div>

<style>
  .combo-header { display:flex; align-items:center; justify-content:space-between; gap:12px; }
  .combo-header h3 { margin:0; font-size:18px; }

  .combo{border:1px solid #ececec;border-radius:10px;padding:16px}
  .combo h3{margin:0 0 10px;font-size:18px}
  .row{display:grid;grid-template-columns:1fr 1fr;gap:12px;margin-bottom:10px}
  .field{display:flex;flex-direction:column;gap:6px}
  .field label{font-size:14px}
  select{height:44px;border:1px solid #d9d9d9;border-radius:8px;padding:0 10px;background:#fff}
  .btn{margin-top:20px;height:48px;border:0;border-radius:10px;background:#111;color:#fff;cursor:pointer;padding:0 18px;width:100%}
  .btn[disabled]{opacity:.5;cursor:not-allowed}
  .msg{margin-top:8px;font-size:14px}
  .msg.error{color:#c62828}
  .msg.success{color:#2e7d32}
  .btn.ks-chart-modal-button{background-color:#fff !important;border:none;bottom:15px;position:relative;}
</style>

<script id="comboConfig" type="application/json">
{
  "sizeOptionNames": ["Size","Waist Size","尺寸","Talla","Tamanho"],
  "rows": {
    "1": {{ combo_ref.value.color_size_1 | strip | json }},
    "2": {{ combo_ref.value.color_size_2 | strip | json }},
    "3": {{ combo_ref.value.color_size_3 | strip | json }},
    "4": {{ combo_ref.value.color_size_4 | strip | json }},
    "5": {{ combo_ref.value.color_size_5 | strip | json }}
  }
}
</script>

<script>
/** JS unchanged except loop limit updated to 5 **/

const $msg = document.getElementById("comboMsg");
function setMsg(t,type=""){ if($msg){ $msg.textContent=t; $msg.className="msg "+(type||""); } }

function fromMetaobjectJSON(){
  const el=document.getElementById("comboConfig");
  try{ return JSON.parse(el.textContent||"null"); } catch(e){ return null; }
}

function normalizeField(i){ if(i==null) return ""; if(typeof i==="string") return i; if(typeof i==="object"&&typeof i.value==="string")return i.value; return String(i||""); }

function parseColorHandleList(raw){
  let text=normalizeField(raw).replace(/<br\s*\/?>/gi,"\n");
  if(!text.trim()) return {};
  const tokens=text.replace(/\r?\n/g," | ").split("|").map(t=>t.trim()).filter(Boolean);
  const map={};
  for(let i=0;i<tokens.length;i+=2){
    const c=tokens[i], h=tokens[i+1]||"";
    if(!c||!h) continue;
    if(/^\d+$/.test(h)) continue;
    map[c]=h;
  }
  return map;
}

async function fetchProductByHandle(h){
  const r=await fetch(`/products/${encodeURIComponent(h)}.js`);
  if(!r.ok) throw new Error();
  return r.json();
}

function detectSizeIndex(p,list){
  const names=(p.options||[]).map(o=>(o.name||"").toLowerCase());
  const wanted=list.map(s=>s.toLowerCase());
  for(let i=0;i<names.length;i++) if(wanted.includes(names[i])) return i+1;
  return names.length===1?1:2;
}
function extractAvailableSizes(p,list){
  const idx=detectSizeIndex(p,list), key="option"+idx;
  const sizes=[];
  for(const v of p.variants||[]) if(v.available) sizes.push(v[key]);
  return [...new Set(sizes)];
}
function findVariantIdBySize(p,size,list){
  const idx=detectSizeIndex(p,list), key="option"+idx;
  const v=(p.variants||[]).find(v=>v.available&&v[key]===size);
  return v?v.id:null;
}

(function initCombo(){
  const meta=fromMetaobjectJSON()||{};
  const sizeOptionNames=meta.sizeOptionNames||[];
  const rowsCfg=[];

  for(let i=1;i<=5;i++){   // <-- updated from 4 to 5
    const raw=meta.rows?.[String(i)];
    const map=parseColorHandleList(raw);
    if(Object.keys(map).length) rowsCfg.push({ colors: map });
  }

  const allRows=document.querySelectorAll("#comboRoot .row");
  allRows.forEach((r,idx)=>{
    if(idx>=rowsCfg.length) r.style.display="none";
    else{
      const cfg=rowsCfg[idx];
      const colorSel=r.querySelector(".color");
      const sizeSel=r.querySelector(".size");
      colorSel.innerHTML=`<option value="">Select</option>`;
      sizeSel.innerHTML=`<option value="">Select</option>`;
      sizeSel.disabled=true;
      Object.keys(cfg.colors).forEach(name=>{
        const o=document.createElement("option");
        o.value=name; o.textContent=name; colorSel.appendChild(o);
      });
      colorSel.addEventListener("change",async()=>{
        sizeSel.innerHTML=`<option value="">Select</option>`; sizeSel.disabled=true;
        const c=colorSel.value; if(!c) return;
        try{
          const p=await fetchProductByHandle(cfg.colors[c]);
          cfg.__productCache??={}; cfg.__productCache[c]=p;
          const sizes=extractAvailableSizes(p,sizeOptionNames);
          sizes.forEach(s=>{ const o=document.createElement("option"); o.value=s; o.textContent=s; sizeSel.appendChild(o);});
          if(sizes.length) sizeSel.disabled=false;
        }catch(e){ setMsg(`${c} color is out of stock`,"error"); }
      });
    }
  });

  document.getElementById("comboAdd")?.addEventListener("click",async()=>{
    const items=[];
    for(let i=0;i<rowsCfg.length;i++){
      const row=document.querySelector(`.row[data-index="${i+1}"]`);
      const c=row.querySelector(".color").value;
      const s=row.querySelector(".size").value;
      if(!c||!s){ setMsg(`Please select Color and Size for row ${i+1}.`,"error"); return; }
      const cfg=rowsCfg[i];
      const p=cfg.__productCache?.[c]||await fetchProductByHandle(cfg.colors[c]);
      const vid=findVariantIdBySize(p,s,sizeOptionNames);
      if(!vid){ setMsg(`Selected size unavailable for ${c}.`,"error"); return; }

      items.push({
        id: vid,
        quantity: 1,
        properties:{
          "_flyBundles:OfferId": "FBT-515065_JKL",
          "_flyBundles:prodQty":"1"
        }
      });
    }

    try{
      await fetch("/cart/add.js",{ method:"POST", headers:{ "Content-Type":"application/json"}, body:JSON.stringify({items}) });
      setMsg("Added to cart!","success");
      if(window.Gokwik?.openCart) window.Gokwik.openCart();
      else if(window.gokwik?.openCart) window.gokwik.openCart();
    }catch(e){
      setMsg("Could not add to cart.","error");
    }
  });
})();
</script>

{% endif %}

{% comment %}
  Bundle Offers block
  - Hides on specific titles
  - Uses product.handle to decide which bundle(s) to show:
      * handle contains 'pack-of-2'  -> only BUY2
      * handle contains 'pack-of-4'  -> only BUY4
  - Shows compare_at_price as slashed price
  - Shows base price as "Discounted Price"
{% endcomment %}

{%- assign t = product.title | downcase -%}
{%- assign h = product.handle | downcase -%}
{%- assign hide_bundle = false -%}
{%- assign show_buy2 = true -%}
{%- assign show_buy4 = true -%}

{%- comment -%} Handle-based bundle visibility {%- endcomment -%}
{%- if h contains 'pick-any-2' or t contains 'pick any 2' -%}
  {%- assign show_buy4 = false -%}
{%- elsif h contains 'pick-any-4' or t contains 'pick any 4' -%}
  {%- assign show_buy2 = false -%}
{%- endif -%}

{%- unless hide_bundle -%}

  {%- assign v = product.selected_or_first_available_variant -%}
  {%- assign price = v.price | divided_by: 100 | round -%}
  {%- assign old_price = v.compare_at_price | divided_by: 100 | round -%}

  {%- assign buy2_discounted = price -%}
  {%- assign buy2_old = old_price -%}

  {%- assign buy4_base = old_price -%}
  {%- assign buy4_discounted = price -%}

  <div class="dynamic-offers-block">
    <div class="dynamic-offers-title">BUNDLE OFFERS</div>

    {%- if show_buy2 -%}
    <!-- Buy 2 bundle -->
    <div class="dynamic-offer-row">
      <div class="dynamic-offer-icon">
        <img src="https://cdn.shopify.com/s/files/1/0450/3476/6485/files/Group_1171275287_4.svg?v=1753706385" alt="Buy 2 icon">
      </div>
      <div class="dynamic-offer-content">
        <div>
          Buy this bundle, get <b>Extra 15% OFF</b>
        </div>
        <div>
          Discounted Price: <b>Rs. {{ buy2_discounted }}</b>
          <span class="dynamic-offer-old">Rs. {{ buy2_old }}</span>
        </div>
        <div class="coupon-green-pill">
          Discount Coupon :
          <button class="coupon-pill-btn" data-code="BUY2" type="button">
            <span class="coupon-pill-code">BUY2</span>
          </button>
          <span class="coupon-copied-pill">Copied!</span>
        </div>
      </div>
    </div>
    {%- endif -%}

    {%- if show_buy2 and show_buy4 -%}
      <div class="dynamic-offer-divider"></div>
    {%- endif -%}

    {%- if show_buy4 -%}
    <!-- Buy 4 bundle -->
    <div class="dynamic-offer-row">
      <div class="dynamic-offer-icon">
        <img src="https://cdn.shopify.com/s/files/1/0450/3476/6485/files/Group_1171275287_4.svg?v=1753706385" alt="Buy 4 icon">
      </div>
      <div class="dynamic-offer-content">
        <div>
          Buy another bundle, get <b>Extra 25% OFF</b>
        </div>
        <div>
          Discounted Price: <b>Rs. {{ buy4_discounted }}</b>
          <span class="dynamic-offer-old">Rs. {{ buy4_base }}</span>
        </div>
        <div class="coupon-green-pill">
          Discount Coupon :
          <button class="coupon-pill-btn" data-code="BUY4" type="button">
            <span class="coupon-pill-code">BUY4</span>
          </button>
          <span class="coupon-copied-pill">Copied!</span>
        </div>
      </div>
    </div>
    {%- endif -%}
  </div>

  <style>
    .coupon-green-pill {
      background: #f0fffa;
      border: 2px solid #15a05a;
      border-radius: 9px;
      display: inline-flex;
      align-items: center;
      padding: 2px 16px;
      margin-top: 7px;
      font-size: 13px;
      color: #118242;
      font-weight: 500;
      gap: 0;
      position: relative;
      transition: border-color 0.2s;
    }
    .coupon-pill-btn {
      background: none;
      border: none;
      padding: 0 2px;
      color: #118242;
      font-weight: 700;
      font-size: 22px;
      cursor: pointer;
      outline: none;
      position: relative;
      margin-left: 6px;
      letter-spacing: 1px;
      transition: color 0.15s;
    }
    .coupon-pill-code { color: #000; font-weight: 700; font-size: 18px; letter-spacing: 1px; padding: 0 3px; }
    .coupon-copied-pill { display: none; color: #15a05a; font-size: 15px; margin-left: 15px; font-weight: 700; }
    .coupon-pill-btn.copied + .coupon-copied-pill { display: inline; }
    .coupon-pill-btn.copied .coupon-pill-code { opacity: 0.45; }

    @media (max-width: 600px) {
      .coupon-green-pill { font-size: 12px; padding: 1px 6px; }
      .coupon-pill-code { font-size: 11px; }
      .coupon-pill-btn  { font-size: 12px; }
    }
    .dynamic-offers-block { max-width: 420px; font-family: 'Inter', Arial, sans-serif; background: #fff; border-radius: 8px; padding: 12px 8px 4px; }
    .dynamic-offers-title { font-weight: 700; font-size: 16px; letter-spacing: 1px; color: #232323; margin-bottom: 14px; margin-left: 4px; }
    .dynamic-offer-row { display: flex; align-items: flex-start; margin-bottom: 8px; }
    .dynamic-offer-icon { width: 42px; height: 42px; min-width: 42px; min-height: 42px; background: #fff; border-radius: 50%; display: flex; align-items: center; justify-content: center; margin-right: 13px; }
    .dynamic-offer-icon img { width: 28px; height: 28px; object-fit: contain; }
    .dynamic-offer-content { flex: 1; font-size: 15px; color: #232323; }
    .dynamic-offer-content > div:first-child { font-size: 15px; font-weight: 500; margin-bottom: 3px; }
    .dynamic-offer-content b { font-weight: 700; }
    .dynamic-offer-content .dynamic-offer-old { text-decoration: line-through; color: #b0b0b0; margin-left: 6px; font-size: 14px; font-weight: 400; }
    .dynamic-offer-divider { border-bottom: 1px solid #ececec; margin: 7px 0 12px 55px; }

    @media (max-width: 600px) {
      .dynamic-offers-block { max-width:100%; padding: 10px 2px; }
      .dynamic-offer-content { font-size:13px; }
      .dynamic-offer-content > div:first-child { font-size: 13px; }
      .dynamic-offers-title { font-size: 15px; }
    }
  </style>

  <script>
    document.addEventListener('DOMContentLoaded', function() {
      document.querySelectorAll('.coupon-pill-btn').forEach(function(btn) {
        btn.addEventListener('click', function() {
          var code = this.getAttribute('data-code');
          if (navigator.clipboard && navigator.clipboard.writeText) {
            navigator.clipboard.writeText(code);
          } else {
            var tempInput = document.createElement('input');
            tempInput.value = code;
            document.body.appendChild(tempInput);
            tempInput.select();
            document.execCommand('copy');
            document.body.removeChild(tempInput);
          }
          btn.classList.add('copied');
          setTimeout(function() { btn.classList.remove('copied'); }, 1100);
        });
      });
    });
  </script>

{%- endunless -%}

{%- comment -%}
Renders the carousel only if the PRODUCT metafield exists and has files.
Metafield: product.metafields.custom.specification_image (List of files)
{%- endcomment -%}

{%- if product and product.metafields.custom.specification_image
  and product.metafields.custom.specification_image.value != blank -%}

  <div class="wysb_container">
    <h2 class="ps_heading">Why you will love this?</h2>

    <div class="floating-controls-container floating-controls-container--inside floating-controls-container--on-hover">
      {%- assign carousel_id = 'carousel-' | append: section.id -%}

      <scroll-carousel
        id="{{ carousel_id }}"
        group-cells
        allow-drag
        class="collection-list scroll-area bleed lg:unbleed"
      >
        {%- for image in product.metafields.custom.specification_image.value -%}
          {%- if image != blank -%}
            <div class="image_box">
              {{ image | image_url: width: 260 | image_tag }}
            </div>
          {%- endif -%}
        {%- endfor -%}

        {%- if section.settings.offer_image != blank -%}
          <div class="image_box">
            {{ section.settings.offer_image | image_url: width: 260 | image_tag }}
          </div>
        {%- endif -%}
      </scroll-carousel>

      {%- capture circle_button_style -%}
        --circle-button-background: {{ section.settings.circle_button_background.rgb }};
        --circle-button-text-color: {{ section.settings.circle_button_text_color.rgb }}
      {%- endcapture -%}

      <button
        is="carousel-prev-button"
        type="button"
        class="prev-next-button prev-next-button--prev circle-button circle-button--lg hover:animate-icon-inline"
        aria-controls="{{ carousel_id }}"
        disabled
        style="{{ circle_button_style }}"
      >
        <span class="sr-only">{{ 'general.accessibility.previous' | t }}</span>
        {%- render 'icon' with 'arrow-left', direction_aware: true -%}
      </button>

      <button
        is="carousel-next-button"
        type="button"
        class="prev-next-button prev-next-button--next circle-button circle-button--lg hover:animate-icon-inline"
        aria-controls="{{ carousel_id }}"
        style="{{ circle_button_style }}"
      >
        <span class="sr-only">{{ 'general.accessibility.next' | t }}</span>
        {%- render 'icon' with 'arrow-right', direction_aware: true -%}
      </button>
    </div>
  </div>
{%- endif -%}
