{%- comment -%} — Normalize title; current URL — {%- endcomment -%}
{% assign normalized_title = product.title | downcase | replace: '
', ' ' | replace: '  ', ' ' | replace: '  ', ' ' %}
{% assign current_url = product.url %}

{%- comment -%} — Metafield product references (Product type) — {%- endcomment -%}
{% assign single_product = product.metafields.custom.single_product_toggle.value %}
{% assign pack3_product  = product.metafields.custom.pack_of_3_product_toggle.value %}

{%- comment -%} — Draft checks — {%- endcomment -%}
{% assign is_draft = false %}
{% if product.status and product.status == 'draft' %}{% assign is_draft = true %}{% endif %}

{% assign single_is_draft = false %}
{% if single_product and single_product.status == 'draft' %}{% assign single_is_draft = true %}{% endif %}

{% assign pack3_is_draft = false %}
{% if pack3_product and pack3_product.status == 'draft' %}{% assign pack3_is_draft = true %}{% endif %}

{%- comment -%} — Valid flags — {%- endcomment -%}
{% assign has_single_valid = false %}
{% if single_product and single_is_draft == false %}{% assign has_single_valid = true %}{% endif %}

{% assign has_pack3_valid = false %}
{% if pack3_product and pack3_is_draft == false %}{% assign has_pack3_valid = true %}{% endif %}

{%- comment -%}
  — Determine ACTIVE tab based on CURRENT product (no parentheses in IFs) —
{%- endcomment -%}
{% assign is_current_single = false %}
{% assign is_current_pack3  = false %}

{% if has_single_valid %}
  {% if product.id == single_product.id %}
    {% assign is_current_single = true %}
  {% endif %}
  {% if is_current_single == false and current_url == single_product.url %}
    {% assign is_current_single = true %}
  {% endif %}
{% endif %}

{% if has_pack3_valid %}
  {% if product.id == pack3_product.id %}
    {% assign is_current_pack3 = true %}
  {% endif %}
  {% if is_current_pack3 == false and current_url == pack3_product.url %}
    {% assign is_current_pack3 = true %}
  {% endif %}
{% endif %}

{%- comment -%} Fallback if none matched {%- endcomment -%}
{% if is_current_single == false and is_current_pack3 == false %}
  {% assign is_current_single = true %}
{% endif %}

{%- comment -%} — Show row? — {%- endcomment -%}
{% assign show_pack_toggle = false %}
{% if is_draft == false %}
  {% if has_single_valid or has_pack3_valid %}
    {% assign show_pack_toggle = true %}
  {% endif %}
{% endif %}

{% if show_pack_toggle %}
<style>
  .toggle-container { display:flex; gap:10px; margin-top:10px; flex-wrap:wrap; }
  .toggle-row { margin-top:14px; }
  .toggle-row label { display:inline-block; min-width:80px; font-weight:600; }
  .toggle-btn {
    padding:5px 20px; border:1px solid #ccc; border-radius:5px; cursor:pointer;
    user-select:none; background:#fff; color:#000; transition:all .3s ease;
  }
  .toggle-btn.active { background:#000; color:#fff; border-color:#000; }
  .toggle-btn.disabled { opacity:.5; pointer-events:none; }
  @media (min-width:786px){ .toggle-btn { padding:3px 30px; font-size:16px; } }
</style>

<div id="packToggle"
     data-current-url="{{ current_url }}"
     {% if has_single_valid %}data-single-url="{{ single_product.url }}"{% endif %}
     {% if has_pack3_valid %}data-pack3-url="{{ pack3_product.url }}"{% endif %}
     data-active="{% if is_current_single %}single{% else %}pack3{% endif %}">
  <div class="toggle-row">
    <label>Pack :</label>
    <div class="toggle-container">
      <button type="button" id="btnSingle"
        class="toggle-btn {% unless has_single_valid %}disabled{% endunless %}{% if is_current_single %} active{% endif %}">
        Single
      </button>

      <button type="button" id="btnPack3"
        class="toggle-btn {% unless has_pack3_valid %}disabled{% endunless %}{% if is_current_pack3 %} active{% endif %}">
        Pack of 3
      </button>
    </div>
  </div>
</div>

<script>
{% raw %}
(function(){
  var root = document.getElementById('packToggle');
  if(!root) return;

  // Data from Liquid
  var currentUrl = root.getAttribute('data-current-url') || '';
  var singleLink = root.getAttribute('data-single-url') || '';
  var pack3Link  = root.getAttribute('data-pack3-url') || '';
  var activeType = root.getAttribute('data-active') || 'single';

  // URL helpers
  function getParam(name){
    var m = new RegExp('[?&]'+name+'=([^&#]*)').exec(window.location.href);
    return m ? decodeURIComponent(m[1].replace(/\+/g, ' ')) : null;
  }
  function appendParam(url, key, value){
    if(!url) return '';
    var hashIndex = url.indexOf('#');
    var base = hashIndex >= 0 ? url.slice(0, hashIndex) : url;
    var hash = hashIndex >= 0 ? url.slice(hashIndex) : '';
    base += (base.indexOf('?') >= 0 ? '&' : '?') + encodeURIComponent(key) + '=' + encodeURIComponent(value);
    return base + hash;
  }

  // Carry return_single across pack pages
  var returnSingleParam = getParam('return_single');

  function determineReturnSingle(){
    if(returnSingleParam) return returnSingleParam;         // keep forwarding
    if(activeType === 'single' && currentUrl) return currentUrl; // current page if single
    if(singleLink) return singleLink;                       // fallback: single metafield
    return currentUrl || '/';                               // last resort
  }

  var btnSingle = document.getElementById('btnSingle');
  var btnPack3  = document.getElementById('btnPack3');

  if(btnSingle){
    btnSingle.addEventListener('click', function(){
      // Prefer remembered Single URL (exact color)
      if(returnSingleParam){
        if(window.location.href !== returnSingleParam){ window.location.href = returnSingleParam; }
        return;
      }
      // If not on Single, go to Single link (or stay if already single)
      if(activeType !== 'single'){
        var target = singleLink || currentUrl || '/';
        if(window.location.href !== target){ window.location.href = target; }
      }
    });
  }

  if(btnPack3){
    btnPack3.addEventListener('click', function(){
      if(!pack3Link) return;
      var ret = determineReturnSingle();
      var url = appendParam(pack3Link, 'return_single', ret);
      if(window.location.href !== url){ window.location.href = url; }
    });
  }
})();
{% endraw %}
</script>
{% endif %}

{% comment %}
  Bundle Builder – Kiwi Sizing via metafield
  - Reads Kiwi ID from each child product metafield: custom.data_sizing_id_kiwi
  - Accepts values like: "10955666 | 10955666" or "h0955666" or multi-line; sanitized in JS
  - Renders Kiwi chart in a modal with data-display-mode="2"
  - Robust money formatting & bundle UI styled to match the provided mock
{% endcomment %}

{% assign uses_no_decimals = false %}
{% if shop.money_format contains 'amount_no_decimals' %}
  {% assign uses_no_decimals = true %}
{% endif %}

<div id="bundle-builder" class="bundle-builder" data-host-product-id="{{ product.id }}">
  <style>
    .bundle-wrap{max-width:960px;margin:0 auto}
    .bundle-item{display:grid;grid-template-columns:72px 1fr;gap:12px;padding:14px 0;border-top:1px solid #eee}
    .bundle-item:first-child{border-top:0}
    .bundle-media img{width:72px;height:96px;object-fit:cover;border-radius:6px}
    .bundle-title{font-weight:600;margin:0 0 4px;line-height:1.3; font-size: 16px;}

    /* price row: price on left, size chart on right */
    .bundle-prices{display:flex;align-items:center;justify-content:space-between;gap:8px;font-size: 16px;
    margin-top: 12px;}
    .price-left{display:flex;align-items:center;gap:8px}
    .bundle-price{font-weight:700}
    .bundle-compare{text-decoration:line-through;opacity:.6}
    .bundle-save{color:#c00;font-weight:600}

    .size-chart-link{display:inline-flex;align-items:center;gap:6px;font-size:12px;text-decoration:none;cursor:pointer;color:#111}
    .size-chart-link:hover{text-decoration:underline}
    .size-chart-link svg{width:14px;height:14px;flex:0 0 14px}

    .size-row{margin-top:8px}
    .size-label{font-size:12px;opacity:.8}
    .size-buttons{display:flex;flex-wrap:wrap;gap:8px;margin-top:6px}

    /* pill size buttons */
    .size-btn{
      border:1px solid #ddd;border-radius: 5px;
    padding: 6px 12px;
    min-width: 37px;
    height: 31px;
      cursor:pointer;background:#fff;font-size:12px;line-height:1;font-weight:500
    }
    .size-btn:hover{border-color:#bbb}
    .size-btn[disabled]{opacity:.45;cursor:not-allowed}
    .size-btn.active{background:#111;color:#fff !important;border-color:#111;box-shadow:0 0 0 2px rgba(0,0,0,.05) inset }

    /* footer: total row then full-width button */
    .bundle-footer{margin-top:8px;padding-top:12px;border-top:1px solid #eee;display:flex;flex-direction:column;gap:12px}
    .bundle-total-row{display:flex;align-items:center;justify-content:space-between;font-size:14px}
    .bundle-total-title{opacity:.8}
    .bundle-total-amount{display:flex;align-items:center;gap:8px;font-weight:700}
    .bundle-total-amount .bundle-compare{text-decoration:line-through;opacity:.6;font-weight:400}
    .bundle-total-amount .bundle-save{color:#c00;font-weight:600}

    .bundle-add{width:100%;background:#111;color:#fff;border:0;border-radius:8px;padding:14px 18px;font-weight:800;letter-spacing:.4px;cursor:pointer}
    .bundle-add:disabled{opacity:.6;cursor:not-allowed}

    /* Kiwi modal */
    .kb-modal{position:fixed;inset:0;display:none;align-items:center;justify-content:center;background:rgba(0,0,0,.5);z-index:9999}
    .kb-modal.open{display:flex}
    .kb-dialog{background:#fff;max-width:760px;width:92vw;max-height:84vh;overflow:auto;border-radius:10px;padding:16px}
    .kb-close{float:right;font-size:18px;cursor:pointer}
  </style>

  {% assign bundle_items = product.metafields.custom.bundle_products.value %}
  {% if bundle_items == blank %}
    <div class="bundle-wrap">
      <p>No bundle items configured. Add products to <strong>custom.bundle_products</strong> metafield.</p>
    </div>
  {% else %}
  <div class="bundle-wrap"
       data-money-format="{{ shop.money_format | escape }}"
       data-no-decimals="{{ uses_no_decimals }}">

    {% for p in bundle_items %}
      {% assign kiwi_id_raw = p.metafields.custom.data_sizing_id_kiwi %}
      <div class="bundle-item"
           data-product-url="{{ p.url }}"
           data-product-id="{{ p.id }}"
           data-kiwi-id="{{ kiwi_id_raw | escape }}">
        <div class="bundle-media">
          <img src="{{ p.featured_image | image_url: width:144 }}" alt="{{ p.title | escape }}">
        </div>
        <div class="bundle-body">
          <div class="bundle-title">{{ p.title }}</div>

          <div class="bundle-prices">
            <span class="price-left">
              <span class="bundle-price" data-price>—</span>
              <span class="bundle-compare" data-compare style="display:none"></span>
              <span class="bundle-save" data-save style="display:none"></span>
            </span>

            {% if kiwi_id_raw != blank %}
              <span class="size-chart-link" data-open-kiwi>
                <svg viewBox="0 0 24 24" fill="none" aria-hidden="true">
                  <path d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04a1.003 1.003 0 0 0 0-1.42L18.38 3.29a1.003 1.003 0 0 0-1.42 0l-1.83 1.83 3.75 3.75 1.83-1.83z" stroke="currentColor" stroke-width="1" />
                </svg>
                SIZE CHART
              </span>
            {% endif %}
          </div>

          <div class="size-row">
            <div class="size-label">Size</div>
            <div class="size-buttons" data-sizes></div>
          </div>

          <input type="hidden" data-selected-variant>
        </div>
      </div>
    {% endfor %}

    <div class="bundle-footer">
      <div class="bundle-total-row">
        <span class="bundle-total-title">Total price</span>
        <span class="bundle-total-amount">
          <span id="bundle-total-price">—</span>
          <span id="bundle-total-compare" class="bundle-compare" style="display:none"></span>
          <span id="bundle-total-save" class="bundle-save" style="display:none"></span>
        </span>
      </div>
      <button class="bundle-add" id="bundle-add">ADD TO CART</button>
    </div>
  </div>
  {% endif %}

  <!-- Kiwi modal -->
  <div class="kb-modal" id="kb-modal" aria-modal="true" role="dialog">
    <div class="kb-dialog">
      <span class="kb-close" id="kb-close" aria-label="Close">✕</span>
      <div id="kb-slot"></div>
    </div>
  </div>
</div>

<script>
(function(){
  var wrap = document.querySelector('.bundle-wrap');
  if(!wrap) return;

  var moneyFormat = wrap.dataset.moneyFormat || '{{ shop.money_format | escape }}';
  var noDecimals  = (wrap.dataset.noDecimals === 'true');

  // ---- money formatting ----
  function formatMoney(cents){
    var amount = noDecimals ? (cents/100).toFixed(0) : (cents/100).toFixed(2);
    var tokenRegex = /\{\{\s*amount(?:_no_decimals|_with_comma_separator)?\s*\}\}/i;
    if(!tokenRegex.test(moneyFormat)) return '₹ ' + amount;
    return moneyFormat.replace(tokenRegex, amount);
  }
  function percentOff(price, compare){
    if(!compare || compare <= price) return null;
    var p = Math.round((1 - (price/compare)) * 100);
    return p > 0 ? p : null;
  }

  // ---- helpers ----
  async function fetchJSON(url){
    var res = await fetch(url, {credentials:'same-origin'});
    if(!res.ok) throw new Error('Failed ' + url);
    return res.json();
  }

  // Accept "10955666 | 10955666", "h0955666", newlines, spaces -> pick first numeric token
  function extractFirstKiwiId(raw){
    if (!raw) return '';
    var s = String(raw).replace(/\n+/g, ' ').trim();
    var parts = s.split(/[\|\s,;]+/).filter(Boolean);
    for (var i=0;i<parts.length;i++){
      var digits = parts[i].replace(/[^\d]/g,'');
      if (digits) return digits;
    }
    return '';
  }

  // ---- build each bundle item ----
  var itemEls = Array.from(document.querySelectorAll('.bundle-item'));
  var bundleState = {}; // productId -> { selectedVariantId, price, compare }

  Promise.all(itemEls.map(async function(el){
    var url = el.dataset.productUrl + '.js';
    var data = await fetchJSON(url);

    var sizeIndex = (data.options || []).findIndex(function(o){ return (/size/i).test(o.name); });
    var variants = data.variants || [];
    var sizesRoot = el.querySelector('[data-sizes]');

    var seen = {};
    variants.forEach(function(v){
      var value = (sizeIndex >= 0 ? v.options[sizeIndex] : v.title) || v.title;
      if(seen[value]) return;
      var availableVariant = variants.find(function(x){
        var xv = (sizeIndex >= 0) ? x.options[sizeIndex] : x.title;
        return xv === value && x.available;
      });
      var b = document.createElement('button');
      b.className = 'size-btn';
      b.type = 'button';
      b.textContent = value;
      if(!availableVariant){ b.disabled = true; }
      b.addEventListener('click', function(){
        sizesRoot.querySelectorAll('.size-btn').forEach(function(x){ x.classList.remove('active'); });
        b.classList.add('active');
        selectVariant(availableVariant);
      });
      sizesRoot.appendChild(b);
      seen[value] = true;
    });

    var priceEl = el.querySelector('[data-price]');
    var compareEl = el.querySelector('[data-compare]');
    var saveEl = el.querySelector('[data-save]');
    var hidden = el.querySelector('[data-selected-variant]');

    function selectVariant(v){
      if(!v) return;
      hidden.value = v.id;
      priceEl.textContent = formatMoney(v.price);
      var hasCompare = v.compare_at_price && v.compare_at_price > v.price;
      if(hasCompare){
        compareEl.textContent = formatMoney(v.compare_at_price);
        compareEl.style.display = '';
        var pct = percentOff(v.price, v.compare_at_price);
        saveEl.textContent = pct ? ('Save ' + pct + '%') : '';
        saveEl.style.display = pct ? '' : 'none';
      }else{
        compareEl.style.display = 'none';
        saveEl.style.display = 'none';
      }
      bundleState[el.dataset.productId] = {
        selectedVariantId: v.id,
        price: v.price,
        compare: v.compare_at_price || 0
      };
      recalcTotal();
    }

    var firstBtn = sizesRoot.querySelector('.size-btn:not([disabled])');
    if(firstBtn) firstBtn.click();

    // SIZE CHART — only when metafield has a usable ID
    var raw = el.dataset.kiwiId;
    var firstId = extractFirstKiwiId(raw);
    var chartTrigger = el.querySelector('[data-open-kiwi]');
    if (chartTrigger && firstId){
      chartTrigger.addEventListener('click', function(){
        openKiwi(firstId);
      });
    } else if (chartTrigger && !firstId){
      chartTrigger.style.display = 'none';
    }
  })).catch(console.error);

  function recalcTotal(){
    var lines = Object.values(bundleState);
    if(!lines.length) return;
    var total = lines.reduce(function(s,l){ return s + (l && l.price || 0); }, 0);
    var totalCompare = lines.reduce(function(s,l){ return s + ((l && l.compare && l.compare > l.price)? l.compare : 0); }, 0);
    var totalPriceEl = document.getElementById('bundle-total-price');
    var compareEl = document.getElementById('bundle-total-compare');
    var saveEl = document.getElementById('bundle-total-save');
    totalPriceEl.textContent = formatMoney(total);
    if(totalCompare > total){
      compareEl.textContent = formatMoney(totalCompare);
      compareEl.style.display = '';
      var pct = Math.round((1 - (total/totalCompare)) * 100);
      if(pct>0){ saveEl.textContent = 'Save ' + pct + '%'; saveEl.style.display = ''; }
      else { saveEl.style.display = 'none'; }
    }else{
      compareEl.style.display = 'none';
      saveEl.style.display = 'none';
    }
  }

  // ---------- Kiwi modal & renderer ----------
  var modal = document.getElementById('kb-modal');
  var slot  = document.getElementById('kb-slot');
  document.getElementById('kb-close').addEventListener('click', function(){ closeKiwi(); });
  modal.addEventListener('click', function(e){ if(e.target === modal) closeKiwi(); });

  function ensureKiwiScript(onReady){
    if (window.KiwiSizing || window.KiwiSizingChart) { onReady(); return; }
    var existing = document.querySelector('script[src*="kiwisizing"]');
    if (existing){ existing.addEventListener('load', onReady, { once:true }); return; }
    var s = document.createElement('script');
    s.async = true;
    s.src = 'https://cdn.kiwisizing.com/widget/kiwiSizing.js' + (window.Shopify && Shopify.shop ? ('?shop='+encodeURIComponent(Shopify.shop)) : '');
    s.onload = onReady;
    document.head.appendChild(s);
  }
  function forceKiwiRender(){
    try {
      if (window.KiwiSizing?.render) { window.KiwiSizing.render(); return; }
      if (window.KiwiSizing?.inject) { window.KiwiSizing.inject(); return; }
      if (window.KiwiSizingChart?.init) { window.KiwiSizingChart.init(); return; }
      window.dispatchEvent(new Event('kiwi:refresh'));
    } catch(e){ console.warn('Kiwi render attempt failed', e); }
  }

  function openKiwi(kiwiId){
    if (!kiwiId) return;
    slot.innerHTML =
      '<div class="kiwiAllowRegularInjectionSelector" id="KiwiSizingChart" '+
      ' data-sizing-id="'+kiwiId+'" data-display-mode="2" data-recommender-display-mode="0" data-layout-type="0"></div>';
    modal.classList.add('open');
    ensureKiwiScript(function(){ setTimeout(forceKiwiRender, 10); });
  }
  function closeKiwi(){
    modal.classList.remove('open');
    slot.innerHTML = '';
  }

  // ---------- (Optional) Add to cart skeleton ----------
  (function(){
    const btn = document.getElementById('bundle-add');
    let isAdding = false;

    btn.addEventListener('click', async function(){
      if (isAdding) return;
      isAdding = true;
      btn.disabled = true;
      btn.dataset.loading = '1';

      try {
        const variantIds = Array.from(document.querySelectorAll('[data-selected-variant]'))
          .map(i => i.value)
          .filter(Boolean);

        if(!variantIds.length){
          alert('Please select sizes for the bundle items.');
          return;
        }

        const items = variantIds.map(id => ({ id: Number(id), quantity: 1 }));

        // open drawer helper
        function openGoKwikDrawer(){
          try{
            if (window.GoKwik?.cart?.open)   { window.GoKwik.cart.open();   return true; }
            if (window.GoKwik?.cart?.show)   { window.GoKwik.cart.show();   return true; }
            if (window.GoKwik?.cart?.toggle) { window.GoKwik.cart.toggle(true); return true; }
            const launcher = document.querySelector('[data-gokwik-cart-launcher], .gokwik-cart-launcher, [data-kwik-cart-launcher]');
            if (launcher){ launcher.click(); return true; }
          }catch(e){ console.warn('Could not open GoKwik drawer:', e); }
          return false;
        }

        // Preferred path: GoKwik
        if (window.GoKwik?.cart) {
          try {
            if (typeof window.GoKwik.cart.addItems === 'function') {
              await window.GoKwik.cart.addItems(items);
            } else if (typeof window.GoKwik.cart.addOrUpdateItems === 'function') {
              await window.GoKwik.cart.addOrUpdateItems(items);
            } else {
              throw new Error('GoKwik add API not available');
            }
            openGoKwikDrawer();
            return;
          } catch (e) {
            console.warn('GoKwik add failed, falling back to Shopify:', e);
            // fall through
          }
        }

        // Shopify fallback — **sequential** to avoid 422 collisions
        for (const it of items){
          const res = await fetch('/cart/add.js', {
            method: 'POST',
            headers: {'Content-Type':'application/json','Accept':'application/json'},
            body: JSON.stringify({ items: [it] })
          });
          if (!res.ok) {
            // If one line fails, keep trying the rest, but note the failure
            console.warn('Shopify add failed for', it.id, 'status=', res.status);
          }
        }

        // Try to open GoKwik drawer (many setups read /cart)
        openGoKwikDrawer();

      } catch (e) {
        console.error(e);
        alert('Unable to add bundle to cart. Please try again.');
      } finally {
        isAdding = false;           // ALWAYS reset so user can add endlessly
        btn.disabled = false;
        delete btn.dataset.loading;
      }
    });
  })();
})();
</script>

{% comment %}
  Hide bundle block on specific products by title or tag.
  - Matches:
    • "airy linen flared dress"
    • any "men ... cotton pant"
    • "straight fit formal pant"
    • product tagged "short kurta set" or "long kurta set"
{% endcomment %}
{% assign t = product.title | downcase %}
{% assign hide_bundle = false %}

{%- comment -%} Title-based conditions {%- endcomment -%}
{% if t contains 'saadaa mystery box' %}
  {% assign hide_bundle = true %}
<!-- {% elsif t contains 'straight fit formal pant' %}
  {% assign hide_bundle = true %} -->
{% endif %}

{%- comment -%} ✅ New tag-based condition {%- endcomment -%}
{% for tag in product.tags %}
  {% assign tag_lc = tag | downcase %}
  {% if tag_lc == 'short kurta set' or tag_lc == 'long kurta set' or tag_lc == 'men triblend combo' or tag_lc == 'women triblend combo' %}
    {% assign hide_bundle = true %}
    {% break %}
  {% endif %}
{% endfor %}

{% unless hide_bundle %}

  {% assign compare_at_price = product.compare_at_price | divided_by: 100.0 %}
  {% assign buy2_discounted = compare_at_price | times: 0.85 | round %}

  <div class="dynamic-offers-block">
    <div class="dynamic-offers-title">BUNDLE OFFERS</div>

    <!-- Buy 2 bundle -->
    <div class="dynamic-offer-row">
      <div class="dynamic-offer-icon">
        <img src="https://cdn.shopify.com/s/files/1/0450/3476/6485/files/Group_1171275287_4.svg?v=1753706385" alt="Buy 2 icon">
      </div>
      <div class="dynamic-offer-content">
        <div>
          Buy this bundle, get <b>Extra 15% OFF</b>
        </div>
        <div>
          Discounted Price: <b>Rs. {{ buy2_discounted }}</b>
