  <style>
    .coupon-green-pill {
      background: #f0fffa;
      border: 2px solid #15a05a;
      border-radius: 9px;
      display: inline-flex;
      align-items: center;
      padding: 2px 16px;
      margin-top: 7px;
      font-size: 13px;
      color: #118242;
      font-weight: 500;
      position: relative;
      transition: border-color 0.2s;
    }
    .coupon-pill-btn {
      background: none;
      border: none;
      padding: 0 2px;
      color: #118242;
      font-weight: 700;
      font-size: 22px;
      cursor: pointer;
      outline: none;
      margin-left: 6px;
      letter-spacing: 1px;
      transition: color 0.15s;
    }
    .coupon-pill-code {
      color: #000;
      font-weight: 700;
      font-size: 18px;
      letter-spacing: 1px;
      padding: 0 3px;
    }
    .coupon-copied-pill {
      display: none;
      color: #15a05a;
      font-size: 15px;
      margin-left: 15px;
      font-weight: 700;
    }
    .coupon-pill-btn.copied + .coupon-copied-pill {
      display: inline;
    }
    .coupon-pill-btn.copied .coupon-pill-code {
      opacity: 0.45;
    }
    @media (max-width: 600px) {
      .coupon-green-pill { font-size: 12px; padding: 1px 6px; }
      .coupon-pill-code { font-size: 11px; }
      .coupon-pill-btn  { font-size: 12px; }
    }
    .dynamic-offers-block {
      max-width: 420px;
      font-family: 'Inter', Arial, sans-serif;
      background: #fff;
      border-radius: 8px;
      padding: 12px 8px 4px 8px;
    }
    .dynamic-offers-title {
      font-weight: bold;
      font-size: 16px;
      letter-spacing: 1px;
      color: #232323;
      margin-bottom: 14px;
      margin-left: 4px;
    }
    .dynamic-offer-row {
      display: flex;
      align-items: flex-start;
      margin-bottom: 8px;
    }
    .dynamic-offer-icon {
      width: 42px;
      height: 42px;
      min-width: 42px;
      min-height: 42px;
      background: #fff;
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      margin-right: 13px;
    }
    .dynamic-offer-icon img {
      width: 28px;
      height: 28px;
      object-fit: contain;
    }
    .dynamic-offer-content {
      flex: 1;
      font-size: 15px;
      color: #232323;
    }
    .dynamic-offer-content > div:first-child {
      font-size: 15px;
      font-weight: 500;
      margin-bottom: 3px;
    }
    .dynamic-offer-content b { font-weight: 700; }
    .dynamic-offer-content .dynamic-offer-old {
      text-decoration: line-through;
      color: #b0b0b0;
      margin-left: 6px;
      font-size: 14px;
      font-weight: 400;
    }
    @media (max-width: 600px) {
      .dynamic-offers-block { max-width:100%; padding: 10px 2px; }
      .dynamic-offer-content { font-size:13px; }
      .dynamic-offer-content > div:first-child { font-size: 13px; }
      .dynamic-offers-title { font-size: 15px; }
    }
  </style>

  <script>
    document.addEventListener('DOMContentLoaded', function() {
      document.querySelectorAll('.coupon-pill-btn').forEach(function(btn) {
        btn.addEventListener('click', function() {
          var code = this.getAttribute('data-code');
          if (navigator.clipboard && navigator.clipboard.writeText) {
            navigator.clipboard.writeText(code);
          } else {
            var tempInput = document.createElement('input');
            tempInput.value = code;
            document.body.appendChild(tempInput);
            tempInput.select();
            document.execCommand('copy');
            document.body.removeChild(tempInput);
          }
          btn.classList.add('copied');
          setTimeout(function() { btn.classList.remove('copied'); }, 1100);
        });
      });
    });
  </script>

