<script>
/* --------------------------------------------------------
   PRODUCT â†’ OFFER-ID MAPPING (Your Original Mapping)
---------------------------------------------------------*/
const offerIdMap = {
  "pick-any-5-women-cotton-pant": "FBT-565646",
  "pick-any-3-women-cotton-pant": "FBT-310776",
  "pick-any-3-women-airy-linen-short-kurta-1": "FBT-913276",
  "pick-any-3-women-airy-linen-long-kurta": "FBT-394579",
  "pick-any-3-women-viscose-pleated-top-1": "FBT-195250",
  "pick-any-3-women-viscose-palazzo-pant": "FBT-572455",
  "pick-any-3-women-airy-linen-shirt": "FBT-843395",
  "pick-any-3-women-airy-linen-straight-pant": "FBT-509818",
  "pick-any-3-women-airy-linen-flared-dress-1": "FBT-345018",
  "pick-any-2-4-way-stretchable-pant-1": "FBT-514759",
  "pick-any-3-men-airy-linen-long-kurta-1": "FBT-389587"
};

const CURRENT_PRODUCT_HANDLE = "{{ product.handle }}";
const selectedOfferId = offerIdMap[CURRENT_PRODUCT_HANDLE] || "DEFAULT-OFFER-ID";


/* --------------------------------------------------------
   HELPERS
---------------------------------------------------------*/
function setMsg(t, type = "") {
  const msg = document.getElementById("comboMsg");
  if (msg) {
    msg.textContent = t;
    msg.className = "msg " + type;
  }
}

function randomSuffix(len = 6) {
  const chars = "abcdefghijklmnopqrstuvwxyz0123456789";
  let out = "";
  for (let i = 0; i < len; i++) out += chars[Math.floor(Math.random() * chars.length)];
  return out;
}

function generateComboFingerprint(rowsCfg) {
  let fp = [];
  for (let i = 0; i < rowsCfg.length; i++) {
    const row = document.querySelector(`.row[data-index="${i+1}"]`);
    const c = row.querySelector(".color").value;
    const s = row.querySelector(".size").value;
    fp.push(`${c}::${s}`);
  }
  return fp.join("|");
}

function saveLastCombo(fp, offerId) {
  sessionStorage.setItem("lastComboFP", fp);
  sessionStorage.setItem("lastComboOfferId", offerId);
}

function getLastCombo() {
  return {
    fp: sessionStorage.getItem("lastComboFP"),
    offerId: sessionStorage.getItem("lastComboOfferId")
  };
}


/* --------------------------------------------------------
   METAOBJECT PARSE LOGIC (UNCHANGED)
---------------------------------------------------------*/
function fromMetaobjectJSON() {
  try { return JSON.parse(document.getElementById("comboConfig").textContent); }
  catch { return null; }
}

function normalizeField(i) {
  if (i == null) return "";
  if (typeof i === "string") return i;
  if (typeof i === "object" && typeof i.value === "string") return i.value;
  return String(i);
}

function parseColorHandleList(raw) {
  let text = normalizeField(raw).replace(/<br\s*\/?>/gi, "\n");
  if (!text.trim()) return {};
  const parts = text.replace(/\r?\n/g, "|").split("|").map(t => t.trim()).filter(Boolean);
  const map = {};
  for (let i = 0; i < parts.length; i += 2) {
    let c = parts[i], h = parts[i+1];
    if (c && h && !/^\d+$/.test(h)) map[c] = h;
  }
  return map;
}

async function fetchProductByHandle(h) {
  const r = await fetch(`/products/${h}.js`);
  return r.json();
}

function detectSizeIndex(p, list) {
  const names = p.options.map(o => o.name.toLowerCase());
  const want = list.map(s => s.toLowerCase());
  for (let i = 0; i < names.length; i++)
    if (want.includes(names[i])) return i + 1;
  return names.length === 1 ? 1 : 2;
}

function extractAvailableSizes(p, list) {
  const idx = detectSizeIndex(p, list), key = "option"+idx;
  const sizes = [];
  p.variants.forEach(v => { if (v.available) sizes.push(v[key]); });
  return [...new Set(sizes)];
}

function findVariantIdBySize(p, size, list) {
  const idx = detectSizeIndex(p, list), key = "option"+idx;
  const v = p.variants.find(v => v.available && v[key] === size);
  return v ? v.id : null;
}


/* --------------------------------------------------------
   INITIALIZE COMBO DROPDOWNS
---------------------------------------------------------*/
let rowsCfg = [];
let sizeOptionNames = [];

(function initCombo() {

  const meta = fromMetaobjectJSON();
  sizeOptionNames = meta.sizeOptionNames;

  for (let i = 1; i <= 5; i++) {
    const raw = meta.rows[String(i)];
    const map = parseColorHandleList(raw);
    if (Object.keys(map).length) rowsCfg.push({ colors: map });
  }

  document.querySelectorAll("#comboRoot .row").forEach((row, idx) => {
    if (idx >= rowsCfg.length) {
      row.style.display = "none";
      return;
    }

    const cfg = rowsCfg[idx];
    const colorSel = row.querySelector(".color");
    const sizeSel = row.querySelector(".size");

    sizeSel.disabled = true;

    Object.keys(cfg.colors).forEach(c => {
      const o = document.createElement("option");
      o.value = o.textContent = c;
      colorSel.appendChild(o);
    });

    colorSel.addEventListener("change", async () => {
      sizeSel.innerHTML = `<option value="">Select</option>`;
      sizeSel.disabled = true;

      const c = colorSel.value;
      if (!c) return;

      const p = await fetchProductByHandle(cfg.colors[c]);
      cfg.__productCache ??= {};
      cfg.__productCache[c] = p;

      const sizes = extractAvailableSizes(p, sizeOptionNames);
      sizes.forEach(s => {
        const o = document.createElement("option");
        o.value = o.textContent = s;
        sizeSel.appendChild(o);
      });

      sizeSel.disabled = false;
    });
  });

})();


/* --------------------------------------------------------
   ADD TO CART LOGIC (NEW SMART BUNDLE SYSTEM)
---------------------------------------------------------*/
document.getElementById("comboAdd")?.addEventListener("click", async () => {

  const currentFP = generateComboFingerprint(rowsCfg);
  const last = getLastCombo();

  let offerIdToUse = "";

  if (last.fp && last.fp === currentFP) {
    // ðŸ”¥ SAME SELECTION â†’ increase qty
    offerIdToUse = last.offerId;
  } else {
    // ðŸ†• NEW SELECTION â†’ new bundle
    offerIdToUse = selectedOfferId + "_" + randomSuffix();
    saveLastCombo(currentFP, offerIdToUse);
  }

  const items = [];

  for (let i = 0; i < rowsCfg.length; i++) {

    const row = document.querySelector(`.row[data-index="${i+1}"]`);
    const c = row.querySelector(".color").value;
    const s = row.querySelector(".size").value;

    if (!c || !s) {
      setMsg(`Please select Color and Size for row ${i+1}.`, "error");
      return;
    }

    const cfg = rowsCfg[i];
    const p = cfg.__productCache?.[c] || await fetchProductByHandle(cfg.colors[c]);
    const vid = findVariantIdBySize(p, s, sizeOptionNames);

    if (!vid) {
      setMsg(`Selected size unavailable for ${c}.`, "error");
      return;
    }

    items.push({
      id: vid,
      quantity: 1,
      properties: {
        "_flyBundles:OfferId": offerIdToUse,
        "_flyBundles:prodQty": "1",
        "_flyBundles:customField": ""
      }
    });
  }

  // merge duplicates (your original logic)
  let itemsCopy = checkDuplicateItems(JSON.parse(JSON.stringify(items)));

  try {
    await fetch("/cart/add.js", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ items: itemsCopy })
    });

    setMsg("Added to cart!", "success");

    if (window.Gokwik?.openCart) window.Gokwik.openCart();
    else if (window.gokwik?.openCart) window.gokwik.openCart();

  } catch (e) {
    console.error(e);
    setMsg("Could not add to cart.", "error");
  }

});


/* --------------------------------------------------------
   DUPLICATE MERGE (Your original function)
---------------------------------------------------------*/
function checkDuplicateItems(arr) {
  let map = {};
  arr.forEach(item => {
    let id = item.id;
    if (!map[id]) {
      map[id] = {
        qty: 0,
        custom: "",
        offer: item.properties["_flyBundles:OfferId"]
      };
    }
    map[id].qty += item.quantity;
    map[id].custom = item.properties["_flyBundles:customField"];
  });

  return Object.keys(map).map(id => ({
    id,
    quantity: map[id].qty,
    properties: {
      "_flyBundles:customField": map[id].custom,
      "_flyBundles:OfferId": map[id].offer,
      "_flyBundles:prodQty": map[id].qty
    }
  }));
}
</script>




