{% assign is_special_timeline = false %}
{% if product.tags contains 'SDELPT' %}
  {% assign is_special_timeline = true %}
{% endif %}

<div class="ot" aria-label="Order status timeline">
  <div class="ot-card">
    <div class="ot-tl">
      <div class="ot-step" aria-current="step">
        <div class="ot-icon">
          <svg viewBox="0 0 24 24" aria-hidden="true">
            <circle cx="9" cy="20" r="1"></circle>
            <circle cx="17" cy="20" r="1"></circle>
            <path d="M3 3h2l2.4 10.4A2 2 0 0 0 9.36 15H18a2 2 0 0 0 2-1.6L22 7H6"></path>
          </svg>
        </div>
        <div class="ot-date" id="orderBy">—</div>
        <div class="ot-label">Order Placed</div>
      </div>

      <div class="ot-step">
        <div class="ot-icon">
          <svg viewBox="0 0 24 24" aria-hidden="true">
            <path d="M3 7h11v8H3z"></path>
            <path d="M14 10h4l3 3v2h-7"></path>
            <circle cx="7" cy="18" r="2"></circle>
            <circle cx="17" cy="18" r="2"></circle>
          </svg>
        </div>
        <div class="ot-date" id="dispatchBy">—</div>
        <div class="ot-label">Order Shipped</div>
      </div>

      <div class="ot-step">
        <div class="ot-icon">
          <svg viewBox="0 0 24 24" aria-hidden="true">
            <path d="M3 7l9-4 9 4-9 4-9-4z"></path>
            <path d="M3 7v10l9 4 9-4V7"></path>
            <path d="M12 11v10"></path>
          </svg>
        </div>
        <div class="ot-date" id="deliveryBy">—</div>
        <div class="ot-label">Estimated Delivery</div>
      </div>
    </div>

  </div>
</div>

<style>
  /* ===== Order Timeline – SCOPED (won't affect the page) ===== */
  .ot { --ot-card:#f6f7f8; --ot-text:#0f172a; --ot-muted:#6b7280; --ot-muted2:#9ca3af; --ot-dot:#0a0a0a; --ot-line:#e5e7eb; }
.ot-card {
    background: var(--ot-card);
    border-radius: 16px;
    padding: 16px 1px 16px;
}
  .ot-tl{
    position:relative;
    display:flex;
    align-items:flex-start;
    justify-content:space-between;
    gap:28px;
    padding:8px 8px 0;
  }

  /* light connector line behind icons (centered on icon circle) */
  .ot-tl::before{
    content:"";
    position:absolute;
    left:64px; right:64px;
    top:30px;            /* aligns with icon center (44px tall) */
    height:2px;
    background:var(--ot-line);
    z-index:0;
  }

  .ot-step{
    flex:1 1 0;
    display:flex;
    flex-direction:column;
    align-items:center;
    text-align:center;
    min-width:0;
  }

  .ot-icon{
    display:grid; place-items:center;
    width:44px; height:44px; border-radius:999px;
    background:var(--ot-dot);
    position:relative; z-index:1; /* above the line */
  }
  .ot-icon svg{ width:22px; height:22px; fill:none; stroke:#fff; stroke-width:2; stroke-linecap:round; stroke-linejoin:round; }

.ot-date {
    margin-top: 10px;
    font-weight: 800;
    font-size: 14px;
    color: var(--ot-text);
    white-space: nowrap;
}
  .ot-label{
    margin-top:1px;
    font-size:12px;
    font-weight:600;
    color:var(--ot-muted);
  }
  .ot-note{
    margin-top:12px;
    font-size:12px;
    color:var(--ot-muted2);
    text-align:center;
  }

  @media (max-width:640px){
    .ot-tl{ gap:16px; }
    .ot-tl::before {
        left: 54px;
        right: 54px;
        top: 27px;
    }
    .ot-icon{ width:40px; height:40px; }
    .ot-date{ font-size:11px; }
    .ot-label{ font-size:10px; }
  }
</style>

<script>
  // ===== Timeline dates (scoped to these IDs) =====
  (function(){
    // Pass Liquid variable to JS
    const isSpecialProduct = {{ is_special_timeline | json }};

    const months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    const el = id => document.getElementById(id);

    function ordinal(n){
      const j=n%10, k=n%100;
      if (k>=11 && k<=13) return n+"th";
      if (j===1) return n+"st";
      if (j===2) return n+"nd";
      if (j===3) return n+"rd";
      return n+"th";
    }
    function addDays(d, days){ const x=new Date(d); x.setDate(x.getDate()+days); return x; }
    function fmt(d){ return `${months[d.getMonth()]} ${ordinal(d.getDate())}`; }
    function range(a,b){
      const sameMonth = a.getMonth()===b.getMonth() && a.getFullYear()===b.getFullYear();
      return sameMonth
        ? `${months[a.getMonth()]} ${ordinal(a.getDate())}\u2013${ordinal(b.getDate())}`
        : `${fmt(a)}\u2009–\u2009${fmt(b)}`;
    }

    function update(){
      const now = new Date();

      // 1️⃣ ORDER PLACED → Always Today
      el('orderBy').textContent = `${months[now.getMonth()]} ${ordinal(now.getDate())}`;

      if (isSpecialProduct) {
        // === SPECIAL LOGIC (Tag: SDELPT) ===
        // Dispatch: Fixed to Feb 5th
        // Delivery: Calculated based on Dispatch (+2 to +5 days)

        // Create date for Feb 5th of current year (Month is 0-indexed, so 1 = Feb)
        const shipDate = new Date(now.getFullYear(), 1, 5); 

        // If Feb 5th has already passed this year, assume next year (optional safety)
        if (now > addDays(shipDate, 2)) {
           shipDate.setFullYear(shipDate.getFullYear() + 1);
        }

        // Set Dispatch Text
        el('dispatchBy').textContent = `${months[shipDate.getMonth()]} ${ordinal(shipDate.getDate())}`;

        // Set Delivery Text (Ship Date +2 days TO Ship Date +5 days)
        const estStart = addDays(shipDate, 2);
        const estEnd   = addDays(shipDate, 5);
        el('deliveryBy').textContent = range(estStart, estEnd);

      } else {
        // === STANDARD LOGIC (No Tag) ===
        // Dispatch: Today - Tomorrow
        // Delivery: Today+2 - Today+5

        el('dispatchBy').textContent = range(addDays(now, 0), addDays(now, 1));
        el('deliveryBy').textContent = range(addDays(now, 2), addDays(now, 5));
      }
    }

    update();
    setInterval(update, 15*60*1000);
  })();
</script>

{%- comment -%}
Renders the carousel only if the PRODUCT metafield exists and has files.
Metafield: product.metafields.custom.specification_image (List of files)
{%- endcomment -%}

{%- if product and product.metafields.custom.specification_image
  and product.metafields.custom.specification_image.value != blank -%}

  <div class="wysb_container">
    <h2 class="ps_heading">Why you will love this?</h2>

    <div class="floating-controls-container floating-controls-container--inside floating-controls-container--on-hover">
      {%- assign carousel_id = 'carousel-' | append: section.id -%}

      <scroll-carousel
        id="{{ carousel_id }}"
        group-cells
        allow-drag
        class="collection-list scroll-area bleed lg:unbleed"
      >
        {%- for image in product.metafields.custom.specification_image.value -%}
          {%- if image != blank -%}
            <div class="image_box">
              {{ image | image_url: width: 260 | image_tag }}
            </div>
          {%- endif -%}
        {%- endfor -%}

        {%- if section.settings.offer_image != blank -%}
          <div class="image_box">
            {{ section.settings.offer_image | image_url: width: 260 | image_tag }}
          </div>
        {%- endif -%}
      </scroll-carousel>

      {%- capture circle_button_style -%}
        --circle-button-background: {{ section.settings.circle_button_background.rgb }};
        --circle-button-text-color: {{ section.settings.circle_button_text_color.rgb }}
      {%- endcapture -%}

      <button
        is="carousel-prev-button"
        type="button"
        class="prev-next-button prev-next-button--prev circle-button circle-button--lg hover:animate-icon-inline"
        aria-controls="{{ carousel_id }}"
        disabled
        style="{{ circle_button_style }}"
      >
        <span class="sr-only">{{ 'general.accessibility.previous' | t }}</span>
        {%- render 'icon' with 'arrow-left', direction_aware: true -%}
      </button>

      <button
        is="carousel-next-button"
        type="button"
        class="prev-next-button prev-next-button--next circle-button circle-button--lg hover:animate-icon-inline"
        aria-controls="{{ carousel_id }}"
        style="{{ circle_button_style }}"
      >
        <span class="sr-only">{{ 'general.accessibility.next' | t }}</span>
        {%- render 'icon' with 'arrow-right', direction_aware: true -%}
      </button>
    </div>
  </div>
{%- endif -%}
