{%- assign form_id = 'product-form-' | append: product.id -%}
{%- form 'product', product, is: 'product-form', id: form_id -%}

  <!-- Variant hidden field -->
  <input
    type="hidden"
    name="id"
    value="{{ product.selected_or_first_available_variant.id }}"
    id="product-variant-id-{{ product.id }}"
  >

  <!-- Quantity Selector -->
  {% render 'quantity-selector',
      variant: product.selected_or_first_available_variant,
      form: form_id %}

  <!-- SIZE BUTTONS -->
  {%- if product.options contains 'Waist Size' -%}
    <div class="size-container">
      <div class="size-buttons" data-product-id="{{ product.id }}">
        {% for variant in product.variants %}
          <button
            type="button"
            class="size-btn {% unless variant.available %}disabled{% endunless %}"
            data-variant-id="{{ variant.id }}"
            data-available="{{ variant.available }}"
            {% unless variant.available %}disabled aria-disabled="true"{% endunless %}
          >
            {{ variant.option1 }}{% unless variant.available %} — Sold Out{% endunless %}
          </button>
        {% endfor %}
      </div>
    </div>
  {%- endif -%}

  <!-- ADD TO CART -->
  <button type="submit" class="button button--primary add-to-cart-btn">
    ADD TO CART
  </button>

{%- endform -%}

<!-- STYLES -->
<style>
  .size-btn.active {
    color: #f1bc2a;
    border-color: #f1bc2a;
  }

  .size-btn.disabled {
    opacity: 0.45;
    cursor: not-allowed !important;
    pointer-events: none;
  }

  .add-to-cart-btn.disabled {
    background: #ccc !important;
    color: #777 !important;
    cursor: not-allowed !important;
    pointer-events: none !important;
padding: 10px 36px;
  }

  .button, 
  .shopify-challenge__button, 
  .shopify-payment-button__button--unbranded {
    padding: 10px 36px !important;
  }



.quantity-selector {

    margin-right: 10px;
}
</style>

<!-- SCRIPT -->
<script>
document.addEventListener("DOMContentLoaded", function() {

  const formEl       = document.getElementById('{{ form_id }}');
  const variantInput = document.getElementById('product-variant-id-{{ product.id }}');
  const sizeButtons  = formEl.querySelectorAll('.size-btn');
  const addToCartBtn = formEl.querySelector('.add-to-cart-btn');

  const variants = {{ product.variants | json }};

  /* -----------------------------
     FUNCTION: UPDATE ATC BUTTON
  ------------------------------*/
  function updateAddToCartState() {
    const selectedId = variantInput.value;
    const selectedVariant = variants.find(v => v.id == selectedId);

    if (!selectedVariant || !selectedVariant.available) {
      addToCartBtn.classList.add("disabled");
      addToCartBtn.disabled = true;
      addToCartBtn.textContent = "SOLD OUT";   // ★ NEW LABEL
    } else {
      addToCartBtn.classList.remove("disabled");
      addToCartBtn.disabled = false;
      addToCartBtn.textContent = "ADD TO CART"; // ★ REVERT LABEL
    }
  }

  /* -----------------------------
      1) Disable quantity +/–
  ------------------------------*/
  formEl.querySelectorAll('.quantity-selector button')
    .forEach(btn => btn.setAttribute('type','button'));

  /* -----------------------------
      2) Size selection
  ------------------------------*/
  sizeButtons.forEach(btn => {
    if (!btn.classList.contains("disabled")) {
      btn.addEventListener('click', function(e) {
        e.preventDefault();

        sizeButtons.forEach(b => b.classList.remove('active'));
        this.classList.add('active');

        variantInput.value = this.dataset.variantId;

        updateAddToCartState();
      });
    }
  });

  /* -----------------------------
      3) Run on load
  ------------------------------*/
  updateAddToCartState();

});
</script>
